>>  닫기버튼 Click시
+-------------------------------------------------------------------------------------------------*/
function btnClose_OnClick(obj)
{
	close(false);
}

/*-------------------------------------------------------------------------------------------------+
>>  금일참여선수 선택시
+-------------------------------------------------------------------------------------------------*/
function chkEnterRacer_OnClick(obj,strValue)
{
	if ( chkEnterRacer.Value ) {
        cboRaceGap.Enable   = true;
        cboRaceRegNo.Enable = true;
	} else {
        cboRaceGap.Enable   = false;
        cboRaceRegNo.Enable = false;
	}
}
