/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutRacerRecd.ClearData();
    dsOutMotRecd.ClearData();
    dsOutBoatRecd.ClearData();
    dsOutMotWeath.ClearData();

    dsOutRacerRecdDetail.ClearData();
    dsOutMotRecdDetail.ClearData();
    dsOutBoatRecdDetail.ClearData();
    dsOutMotWeathDetail.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e02040020-service:searchRacerRecd";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerRecd=dsOutRacerRecd "
                     + "dsOutMotRecd=dsOutMotRecd "
                     + "dsOutBoatRecd=dsOutBoatRecd ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
    fce_AddParam(Trim("ALL_YN   "), tbMain.tbRacer.chkAllYn.Value  );
    fce_AddParam(Trim("STR_YEAR "), tbMain.tbRacer.cboStrYear.Value);
    fce_AddParam(Trim("RACER_NM "), tbMain.tbRacer.edRacerNm.Text  );
    fce_AddParam(Trim("MOT_NO   "), tbMain.tbMot.edMotNo.Text      );
    fce_AddParam(Trim("BOAT_NO  "), tbMain.tbBoat.edBoatNo.Text    );
    fce_AddParam(Trim("ST_MTHD_CD"),cboStMthdCd.value 	); //온라인스타트여부

    if ( tbMain.tbRacer.chkAllYn.Value == "1" ) {
        sServiceName = "e02040020-service:searchRacerRecdAll";
    }

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchRecdCheck() {

	return true;
}

function fcSearchRecd(sType) {
    if ( !fcSearchRecdCheck() ) return;

	var sServiceName = "e02040020-service:searchRecdDetail";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTemp=dsOutRecdDetail ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("ST_MTHD_CD"),cboStMthdCd.value 	); //온라인스타트여부

	if ( sType == "RACER" ) {
	    dsOutRacerRecdDetail.ClearData();
        sOutDataSet  = "dsOutRacerRecdDetail=dsOutRecdDetail ";
        if ( fnc_IsNull(dsOutRacerRecd.GetColumn(dsOutRacerRecd.currow, "RACER_NO")) ) return;
        fce_AddParam(Trim("RACER_NO "), dsOutRacerRecd.GetColumn(dsOutRacerRecd.currow, "RACER_NO") );
	}
	if ( sType == "MOT" ) {
	    dsOutMotRecdDetail.ClearData();
        sOutDataSet  = "dsOutMotRecdDetail=dsOutRecdDetail ";
        if ( fnc_IsNull(dsOutMotRecd.GetColumn(dsOutMotRecd.currow, "MOT_NO")) ) return;
        fce_AddParam(Trim("MOT_NO   "), dsOutMotRecd.GetColumn(dsOutMotRecd.currow, "MOT_NO") );
	}
	if ( sType == "BOAT" ) {
	    dsOutBoatRecdDetail.ClearData();
        sOutDataSet  = "dsOutBoatRecdDetail=dsOutRecdDetail ";
        if ( fnc_IsNull(dsOutBoatRecd.GetColumn(dsOutBoatRecd.currow, "BOAT_NO")) ) return;
        fce_AddParam(Trim("BOAT_NO  "), dsOutBoatRecd.GetColumn(dsOutBoatRecd.currow, "BOAT_NO") );
	}
	if ( sType == "WEATH" ) {
	    dsOutMotWeathDetail.ClearData();
		sServiceName = "e02040020-service:searchWeathDetail";
        sOutDataSet  = "dsOutMotWeathDetail=dsOutRecdDetail ";
        if ( fnc_IsNull(dsOutMotWeath.GetColumn(dsOutMotWeath.currow, "MOT_NO")) ) return;
        fce_AddParam(Trim("MOT_NO   "), dsOutMotWeath.GetColumn(dsOutMotWeath.currow, "MOT_NO") );
		fce_AddParam(Trim("WEATH_CD "), tbMain.tbWeath.cboWeath.Value);
	}

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


