/*-------------------------------------------------------------------------------------------------+
>>  회차변경시
+-------------------------------------------------------------------------------------------------*/
function cboSRaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fcInit();
    var nRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
    var nDayOrd = toInteger(dsOutSRaceTms.GetColumn(nRow, "DAY_ORD"));

    for ( var i = 0; i < nDayOrd; i++ ) {
        tbMain.tbMot.grdMotRecd.SetColProp(1 + i, "Width", 50);
        tbMain.tbWeath.grdMotRecd.SetColProp(1 + i, "Width", 50);
    }
    for ( var i = nDayOrd; i < 7; i++ ) {
        tbMain.tbMot.grdMotRecd.SetColProp(1 + i, "Width",  0);
        tbMain.tbWeath.grdMotRecd.SetColProp(1 + i, "Width",  0);
    }

    //선수탭 착수 조절.
    setRacerGridSize();
    //모터탭 착수 조절.
    setMotGridSize();
    //보트탭 착수 조절.
    setBoatGridSize();
	//기상별 모터기력 변화탭 착수 조절.
	setWeathGridSize();
}

/*-------------------------------------------------------------------------------------------------+
>>  년도변경시
+-------------------------------------------------------------------------------------------------*/
function cboSStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    tbMain.tbRacer.cboStrYear.Value = cboSStndYear.Value;
    fce_GetRaceTms(cboSStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");
}

/*-------------------------------------------------------------------------------------------------+
>>  선수정보Grid에 보여줌
+-------------------------------------------------------------------------------------------------*/
function dsOutRacerRecd_OnRowPosChanged(obj,nOldRow,nRow)
{
    fnc_SetAllColumnValue(dsOutRacerSum, "DESC", "");

    var sTms6AmuRankScr  = toInteger(obj.GetColumn(nRow, "TMS_6_AMU_RANK_SCR"));
    var sTms6AmuAcdntScr = toInteger(obj.GetColumn(nRow, "TMS_6_AMU_ACDNT_SCR"));
    var sTms6RaceCnt     = toInteger(obj.GetColumn(nRow, "TMS_6_RACE_CNT"));
    var sTms6Rank1       = toInteger(obj.GetColumn(nRow, "TMS_6_RANK_1"));
    var sTms6Rank2       = toInteger(obj.GetColumn(nRow, "TMS_6_RANK_2"));
    var sTms6Rank3       = toInteger(obj.GetColumn(nRow, "TMS_6_RANK_3"));

    var sAmuRankScr  = toInteger(obj.GetColumn(nRow, "AMU_RANK_SCR"));
    var sAmuAcdntScr = toInteger(obj.GetColumn(nRow, "AMU_ACDNT_SCR"));
    var sRaceCnt     = toInteger(obj.GetColumn(nRow, "RACE_CNT"));
    var sRank1       = toInteger(obj.GetColumn(nRow, "RANK_1_CNT"));
    var sRank2       = toInteger(obj.GetColumn(nRow, "RANK_2_CNT"));
    var sRank3       = toInteger(obj.GetColumn(nRow, "RANK_3_CNT"));

    var i = 0;
    var sTemp = "";

    // 선수정보
    sTemp = obj.GetColumn(nRow, "RACER_NO")
          + " / " 
          + obj.GetColumn(nRow, "NM_KOR")
          + " / " 
          + obj.GetColumn(nRow, "RACER_GRD_CD");
    dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

    // 출주횟수/승률/연대율/삼연대율
    sTemp = obj.GetColumn(nRow, "RACE_CNT")
          + " / "
          + obj.GetColumn(nRow, "WIN_RATIO")
          + " / " 
          + obj.GetColumn(nRow, "HIGH_RANK_RATIO")
          + " / " 
          + obj.GetColumn(nRow, "HIGH_3_RANK_RATIO");
    dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

    // 모터항주타임
    sTemp = substr(obj.GetColumn(nRow, "TMRAS_MIN_ITRDT_RUN_TM"), 0, 1) + "."
          + substr(obj.GetColumn(nRow, "TMRAS_MIN_ITRDT_RUN_TM"), 1, 2) + "."
          + substr(obj.GetColumn(nRow, "TMRAS_MIN_ITRDT_RUN_TM"), 3   )
          + " / "
          + substr(obj.GetColumn(nRow, "TMRAS_MAX_ITRDT_RUN_TM"), 0, 1) + "."
          + substr(obj.GetColumn(nRow, "TMRAS_MAX_ITRDT_RUN_TM"), 1, 2) + "."
          + substr(obj.GetColumn(nRow, "TMRAS_MAX_ITRDT_RUN_TM"), 3   )
          + " / " 
          + obj.GetColumn(nRow, "TMRAS_ITRDT_RUN_TM_DVTN")
          + " / " 
          + obj.GetColumn(nRow, "TMRAS_AVG_ITRDT_RUN_TM");
    dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

    // 보트항주타임
    sTemp = substr(obj.GetColumn(nRow, "TBRAS_MIN_ITRDT_RUN_TM"), 0, 1) + "."
          + substr(obj.GetColumn(nRow, "TBRAS_MIN_ITRDT_RUN_TM"), 1, 2) + "."
          + substr(obj.GetColumn(nRow, "TBRAS_MIN_ITRDT_RUN_TM"), 3   )
          + " / "
          + substr(obj.GetColumn(nRow, "TBRAS_MAX_ITRDT_RUN_TM"), 0, 1) + "."
          + substr(obj.GetColumn(nRow, "TBRAS_MAX_ITRDT_RUN_TM"), 1, 2) + "."
          + substr(obj.GetColumn(nRow, "TBRAS_MAX_ITRDT_RUN_TM"), 3   )
          + " / " 
          + obj.GetColumn(nRow, "TBRAS_ITRDT_RUN_TM_DVTN")
          + " / " 
          + obj.GetColumn(nRow, "TBRAS_AVG_ITRDT_RUN_TM");
    dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

	fcSearchRecd("RACER");
}

/*-------------------------------------------------------------------------------------------------+
>>  모터정보Grid에 보여줌
+-------------------------------------------------------------------------------------------------*/
function dsOutMotRecd_OnRowPosChanged(obj,nOldRow,nRow)
{
    fnc_SetAllColumnValue(dsOutMotSum, "DESC", "");

    var sAmuRankScr  = toInteger(obj.GetColumn(nRow, "AMU_RANK_SCR"));
    var sRaceCnt     = toInteger(obj.GetColumn(nRow, "RACE_CNT"));
    var sRank1       = toInteger(obj.GetColumn(nRow, "RANK_1_CNT"));
    var sRank2       = toInteger(obj.GetColumn(nRow, "RANK_2_CNT"));
    var sRank3       = toInteger(obj.GetColumn(nRow, "RANK_3_CNT"));

    var i = 0;
    var sTemp = "";

    // 년도별
    if ( sRaceCnt > 0 ) {
        // 착순점
        sTemp = sAmuRankScr 
              + " / " 
              + sRaceCnt 
              + " = " 
              + round(sAmuRankScr / sRaceCnt, 4);
        dsOutMotSum.SetColumn(i++, "DESC", sTemp);

        // 6회차 연대율
        sTemp = "( "
              + sRank1 
              + " + " 
              + sRank2 
              + " ) / " 
              + sRaceCnt 
              + " × " 
              + 100 
              + " = " 
              + round((sRank1 + sRank2) / sRaceCnt * 100, 4);
        dsOutMotSum.SetColumn(i++, "DESC", sTemp);

        // 6회차 3연대율
        sTemp = "( "
              + sRank1 
              + " + " 
              + sRank2 
              + " + " 
              + sRank3 
              + " ) / " 
              + sRaceCnt 
              + " × " 
              + 100 
              + " = " 
              + round((sRank1 + sRank2 + sRank3) / sRaceCnt * 100, 4);
        dsOutMotSum.SetColumn(i++, "DESC", sTemp);
    }

	fcSearchRecd("MOT");
}

