    var sAmuRankScr  = toInteger(obj.GetColumn(nRow, "AMU_RANK_SCR"));
    var sRaceCnt     = toInteger(obj.GetColumn(nRow, "RACE_CNT"));
    var sRank1       = toInteger(obj.GetColumn(nRow, "RANK_1_CNT"));
    var sRank2       = toInteger(obj.GetColumn(nRow, "RANK_2_CNT"));
    var sRank3       = toInteger(obj.GetColumn(nRow, "RANK_3_CNT"));

    var i = 0;
    var sTemp = "";

    // 년도별
    if ( sRaceCnt > 0 ) {
        // 착순점
        sTemp = sAmuRankScr 
              + " / " 
              + sRaceCnt 
              + " = " 
              + round(sAmuRankScr / sRaceCnt, 4);
        dsOutMotWeathSum.SetColumn(i++, "DESC", sTemp);

        // 6회차 연대율
        sTemp = "( "
              + sRank1 
              + " + " 
              + sRank2 
              + " ) / " 
              + sRaceCnt 
              + " × " 
              + 100 
              + " = " 
              + round((sRank1 + sRank2) / sRaceCnt * 100, 4);
        dsOutMotWeathSum.SetColumn(i++, "DESC", sTemp);

        // 6회차 3연대율
        sTemp = "( "
              + sRank1 
              + " + " 
              + sRank2 
              + " + " 
              + sRank3 
              + " ) / " 
              + sRaceCnt 
              + " × " 
              + 100 
              + " = " 
              + round((sRank1 + sRank2 + sRank3) / sRaceCnt * 100, 4);
        dsOutMotWeathSum.SetColumn(i++, "DESC", sTemp);
    }

	fcSearchRecd("WEATH");
}

/*-------------------------------------------------------------------------------------------------+
>>  선수상세성적 Double Click시
+-------------------------------------------------------------------------------------------------*/
function tbMain_tbRacer_grdRacerRecdDetail_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    objDs = object(obj.BindDataset);
	if ( objDs.GetRowCount() == 0 ) return;
	if ( fnc_IsNull(objDs.GetColumn(objDs.currow, "STND_YEAR")) 
	  || fnc_IsNull(objDs.GetColumn(objDs.currow, "MBR_CD"   )) 
	  || fnc_IsNull(objDs.GetColumn(objDs.currow, "TMS"      )) 
	  || fnc_IsNull(objDs.GetColumn(objDs.currow, "DAY_ORD"  )) 
	  || fnc_IsNull(objDs.GetColumn(objDs.currow, "RACE_NO"  )) 
	   ) {
        return;
	}

	var sArg = " Arg_sStndYear=" + quote(objDs.GetColumn(objDs.currow, "STND_YEAR"))
	         + " Arg_sMbrCd="    + quote(objDs.GetColumn(objDs.currow, "MBR_CD"   ))
	         + " Arg_sTms="      + quote(objDs.GetColumn(objDs.currow, "TMS"      ))
	         + " Arg_sDayOrd="   + quote(objDs.GetColumn(objDs.currow, "DAY_ORD"  ))
	         + " Arg_sRaceNo="   + quote(objDs.GetColumn(objDs.currow, "RACE_NO"  ));

    var bReturn = Dialog("form::E02040044.xml", sArg, 700, 360, "");
}

/*-------------------------------------------------------------------------------------------------+
>>  선수상세성적 Double Click시 모터 그래프 보여줌
+-------------------------------------------------------------------------------------------------*/
function tbMain_tbMot_grdMotRecd_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    if ( dsOutMotRecdDetail.rowcount == 0 ) {
        return;
    }

    var logImgUrl = GBL_REPORT_URL + "log.jpg";
    var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
    var sRptNm    = "e02049020_race_grp.jrf";               //레포트 파일명
    var sDataSet  = "E02040020_RACE=dsOutMotRecdDetailCp";   //"UBI 쿼리명, MIP Dataset명"
    var sArg      = " IMG_URL="+logImgUrl; 
    var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

    dsOutMotRecdDetailCp.copy(dsOutMotRecdDetail);
    dsOutMotRecdDetailCp.AddColumn("STR");

    for ( var i = 0; i <= dsOutMotRecdDetailCp.rowcount; i++ ) {
        if ( dsOutMotRecdDetailCp.getColumn(i, "RANK"   ) == "0" ) {
            dsOutMotRecdDetailCp.SetColumn(i, "RANK"   , 0);
        } else {
            dsOutMotRecdDetailCp.SetColumn(i, "RANK"   , toNumber(7 - dsOutMotRecdDetailCp.getColumn(i, "RANK"   )));
        }

        if ( toNumber(1 - dsOutMotRecdDetailCp.getColumn(i, "STAR_TM")) >= 1 ) {
            dsOutMotRecdDetailCp.SetColumn(i, "STAR_TM", 0);
        } else {
            dsOutMotRecdDetailCp.SetColumn(i, "STAR_TM", toNumber(1 - dsOutMotRecdDetailCp.getColumn(i, "STAR_TM")));
        }
        dsOutMotRecdDetailCp.SetColumn(i, "STR"    , i);
    }

    fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

function tbMain_tbWeath_btnMotSearch_OnClick(obj)
{
	var sServiceName = "e02040020-service:searchMotWeath";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutMotWeath=dsOutMotWeath ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
    fce_AddParam(Trim("MOT_NO   "), tbMain.tbWeath.edMotNo.Text    );
    fce_AddParam(Trim("WEATH_CD "), tbMain.tbWeath.cboWeath.Value);
    fce_AddParam(Trim("ST_MTHD_CD"),cboStMthdCd.value 	); //온라인스타트여부

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");	
}


/*-------------------------------------------------------------------------------------------------+
>>  회차별 최고정번수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function setRacerGridSize(){
	/*
		IWORK_SFR-008 경정 선수편성 메뉴 개선  정번8 까지 추가 수정.
		회차에 따른 최고(8까지)정번수 별로 1 ~ 정번수 까지 화면에 표시.
	*/
	var nMaxTmsRunCnt = 8*2;
	var nMaxRaceRegNoCnt = dsOutSRaceTms.GetColumn(cboSRaceTms.Index, "MAX_RACE_REG_NO_CNT");


	if(nMaxRaceRegNoCnt == -1) nMaxRaceRegNoCnt = 8;

	//Alert("nMaxRaceRegNoCnt : " + nMaxRaceRegNoCnt);

	tbMain.tbRacer.grdRacerRecd.Redraw = false;

	//전체적으로 사이즈를 넓힘.
	for ( var i = 0; i < nMaxTmsRunCnt+2; i++ ) {
        tbMain.tbRacer.grdRacerRecd.SetColProp(4 + i, "Width", 38);
    }

    //nMaxTmsRunCnt 부터 nMaxRaceRegNoCnt까지 사이즈를 줄임.
    for ( var i = nMaxTmsRunCnt; i > nMaxRaceRegNoCnt*2; i-- ) {
        tbMain.tbRacer.grdRacerRecd.SetColProp(4 + i-1, "Width", 0);
    }

    tbMain.tbRacer.grdRacerRecd.SetColProp(0, "Width", 44);
    tbMain.tbRacer.grdRacerRecd.SetColProp(1, "Width", 44);
    tbMain.tbRacer.grdRacerRecd.SetColProp(2, "Width", 44);
    tbMain.tbRacer.grdRacerRecd.SetColProp(3, "Width", 44);

    tbMain.tbRacer.grdRacerRecd.Redraw = true;
}

/*-------------------------------------------------------------------------------------------------+
>>  회차별 최고정번수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function setMotGridSize(){
	/*
		IWORK_SFR-008 경정 선수편성 메뉴 개선  정번8 까지 추가 수정.
		회차에 따른 최고(8까지)정번수 별로 1 ~ 정번수 까지 화면에 표시.
	*/
	var nMaxTmsRunCnt = 8;
	var nMaxRaceRegNoCnt = dsOutSRaceTms.GetColumn(cboSRaceTms.Index, "MAX_RACE_REG_NO_CNT");

	//Alert("nMaxRaceRegNoCnt : " + nMaxRaceRegNoCnt);

	tbMain.tbMot.grdMotRecd.Redraw = false;

	var nCol0W = tbMain.tbMot.grdMotRecd.GetColProp(0, "Width");
	var nCol1W = tbMain.tbMot.grdMotRecd.GetColProp(1, "Width");
	var nCol2W = tbMain.tbMot.grdMotRecd.GetColProp(2, "Width");
	var nCol3W = tbMain.tbMot.grdMotRecd.GetColProp(3, "Width");

	//전체적으로 사이즈를 넓힘.
	for ( var i = 0; i < nMaxTmsRunCnt; i++ ) {
        tbMain.tbMot.grdMotRecd.SetColProp(16 + i, "Width", 30);
    }

    //nMaxTmsRunCnt 부터 nMaxRaceRegNoCnt까지 사이즈를 줄임.
    for ( var i = nMaxTmsRunCnt; i > nMaxRaceRegNoCnt; i-- ) {
        tbMain.tbMot.grdMotRecd.SetColProp(16 + i-1, "Width", 0);
    }

    tbMain.tbMot.grdMotRecd.SetColProp(0, "Width", nCol0W);
    tbMain.tbMot.grdMotRecd.SetColProp(1, "Width", nCol1W);
    tbMain.tbMot.grdMotRecd.SetColProp(2, "Width", nCol2W);
    tbMain.tbMot.grdMotRecd.SetColProp(3, "Width", nCol3W);

    tbMain.tbMot.grdMotRecd.Redraw = true;
}

