function setBoatGridSize(){
	/*
		IWORK_SFR-008 °æÁ¤ ¼±¼öÆí¼º ¸Þ´º °³¼±  Á¤¹ø8 ±îÁö Ãß°¡ ¼öÁ¤.
		È¸Â÷¿¡ µû¸¥ ÃÖ°í(8±îÁö)Á¤¹ø¼ö º°·Î 1 ~ Á¤¹ø¼ö ±îÁö È­¸é¿¡ Ç¥½Ã.
	*/
	var nMaxTmsRunCnt = 8;
	var nMaxRaceRegNoCnt = dsOutSRaceTms.GetColumn(cboSRaceTms.Index, "MAX_RACE_REG_NO_CNT");

	//Alert("nMaxRaceRegNoCnt : " + nMaxRaceRegNoCnt);

	tbMain.tbBoat.grdBoatRecd.Redraw = false;

	var nCol0W = tbMain.tbBoat.grdBoatRecd.GetColProp(0, "Width");
	var nCol1W = tbMain.tbBoat.grdBoatRecd.GetColProp(1, "Width");
	var nCol2W = tbMain.tbBoat.grdBoatRecd.GetColProp(2, "Width");
	var nCol3W = tbMain.tbBoat.grdBoatRecd.GetColProp(3, "Width");

	//ÀüÃ¼ÀûÀ¸·Î »çÀÌÁî¸¦ ³ÐÈû.
	for ( var i = 0; i < nMaxTmsRunCnt; i++ ) {
        tbMain.tbBoat.grdBoatRecd.SetColProp(4 + i, "Width", 40);
    }

    //nMaxTmsRunCnt ºÎÅÍ nMaxRaceRegNoCnt±îÁö »çÀÌÁî¸¦ ÁÙÀÓ.
    for ( var i = nMaxTmsRunCnt; i > nMaxRaceRegNoCnt; i-- ) {
        tbMain.tbBoat.grdBoatRecd.SetColProp(4 + i-1, "Width", 0);
    }

    tbMain.tbBoat.grdBoatRecd.SetColProp(0, "Width", nCol0W);
    tbMain.tbBoat.grdBoatRecd.SetColProp(1, "Width", nCol1W);
    tbMain.tbBoat.grdBoatRecd.SetColProp(2, "Width", nCol2W);
    tbMain.tbBoat.grdBoatRecd.SetColProp(3, "Width", nCol3W);

    tbMain.tbBoat.grdBoatRecd.Redraw = true;
}


