var sFormId       = 'E02040040_1';
var sBUTTONLIST   = '';                          //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트

var oLastDs       = null;
var oLastDsRow    = 0;

/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/

// 최초 화면 Load時 처리 할 사항
function fcFormLoadSetting(obj) {

	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	cboStMthdCd.value = Arg_sStMthdCd;
	fcSearch();
}


// 최초 화면 Load時 처리 할 사항
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}


// 조회버튼 이벤트
function fcSearch(obj) {
    var sServiceName = ""; 
    var sInDataSet   = "";
    var sOutDataSet  = "";

	sServiceName = "e02040040-service:searchRacerpopup";
	sInDataSet   = "";
	sOutDataSet  = "dsRacerResult=dsRacerResult dsMotorResult=dsMotorResult dsBoatResult=dsBoatResult";

    fce_AddParam(Trim("STND_YEAR"), 			Arg_sStndYear		); //년도
	fce_AddParam(Trim("MBR_CD"), 				Arg_sMbrcd     		); //경정장코드
//	fce_AddParam(Trim("TMS_DAYORD"), 			Arg_sTms + Arg_sDayOrd); //회차 일차
	fce_AddParam(Trim("RACER_NO"), 				Arg_sRacerNo 		); //선수번호
	fce_AddParam(Trim("MOTOR_NO"), 				Arg_sMotorNo 		); //모터번호
	fce_AddParam(Trim("BOAT_NO"), 				Arg_sBoatNo 		); //보트번호
	fce_AddParam(Trim("ST_MTHD_CD"), 			cboStMthdCd.value 	); //온라인스타트여부

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


// CallBack함수
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 조회 후 
	if ( sServiceName == "e02040040-service:searchRacerpopup" ) {
		//그리드 Head의 TEXT값 변경
		grdRacerResult.SetCellProp("head", 0, "text", "선 수 : " + Arg_sRacerNm + "(" + Arg_sRacerNo + ")") ;
		grdMotorResult.SetCellProp("head", 0, "text", "모 터 : " + Arg_sMotorNo) ;
		grdBoatResult.SetCellProp("head", 0, "text", "보 트 : " + Arg_sBoatNo) ;
		return;
	}	
}


//  엑셀 버튼
function fcToExcel(obj) {
    var sExcelList = "grdRacerResult:선수|grdMotorResult:모터|grdBoatResult:보트";
    fce_OpenExcelListExport(sExcelList);
}

// 화면닫기 버튼
function fcClose(obj) {
	Close();	
}
function cboSStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}
