/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";


	// 경주수, 정번수에 따라 화면크기를 변경하고 스크롤을 생성한다.
	// 최고정번수 셋팅
	for(var i = 0; i < dsOutSRace.GetRowCount(); i++){
		if(maxRegNo < toInteger(dsOutSRace.GetColumn(i, "RACE_REG_NO_CNT"))) maxRegNo = toInteger(dsOutSRace.GetColumn(i, "RACE_REG_NO_CNT"));
	}		

	// 정번수에 따라 화면??이를 변경한다.
	if(maxRegNo>6) Window.width = 1680;

	// 경주수에 따라 화면높이를 변경한다.
	if(dsOutSRace.GetRowCount()>15) Window.height = 1010;


	for(var i = 0; i < dsOutSRace.GetRowCount(); i++){
		CreateGrid_2(dsOutSRace.GetColumn(i, "RACE_NO"), dsOutSRace.GetColumn(i, "RACE_REG_NO_CNT"), dsOutSRace.GetColumn(i, "ST_MTHD_CD"));
	}

    for(var i = 1; i < dsOutSRace.GetRowCount() + 1 ; i++){
        var sChk = "chkRace";
        fcRaceHold2(object(sChk + i), dsOutSRace.GetColumn(i-1, "RACE_NO"), dsOutSRace.GetColumn(i-1, "RACE_REG_NO_CNT"));
    }

    for(var i = 1; i < dsOutSRace.GetRowCount() + 1; i++){
        CreateObject(i);
    }

    fcSetRaceDgre2();

	// 조회
	//fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

