/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutRace.ClearData();
    dsOutVoil.ClearData();
    dsOutVoilDesc.ClearData();
    dsOutVoilDescPrint.ClearData();
    dsOutPayoff.ClearData();
    dsOutOrganAnal.ClearData();
    dsOutRaceAnal.ClearData();
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {

	if(fnc_isNull(cboSStndYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            cboSStndYear.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSMbr.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경정장");
            cboSMbr.SetFocus();
            return false;
    } 
    if(fnc_isNull(cboSRaceTms.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "회차");
            cboSRaceTms.SetFocus();
            return false;
    }

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    fcInit();

    //경주기준정보조회
	var sServiceName = "e02050020-service:searchRace";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRace=dsOutRace "
                     + "dsOutVoil=dsOutVoil "
                     + "dsOutVoilDesc=dsOutVoilDesc "
                     + "dsOutPayoff=dsOutPayoff "
                     + "dsOutRacePayoff=dsOutRacePayoff "
                     + "dsOutOrganAnal=dsOutOrganAnal "
                     + "dsOutRaceAnal=dsOutRaceAnal ";

    fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("MEET_CD  "), '003'             );

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutOrganAnal)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

	var sServiceName = "e02050020-service:saveOrganAnal";
    var sInDataSet   = "dsOutOrganAnal=dsOutOrganAnal:u";
    var sOutDataSet  = "dsOutRace=dsOutRace "
                     + "dsOutVoil=dsOutVoil "
                     + "dsOutVoilDesc=dsOutVoilDesc "
                     + "dsOutPayoff=dsOutPayoff "
                     + "dsOutRacePayoff=dsOutRacePayoff "
                     + "dsOutOrganAnal=dsOutOrganAnal "
                     + "dsOutRaceAnal=dsOutRaceAnal ";

    fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {

	if ( !fcSearchCheck(obj) ) return;

	if ( dsOutRace.rowcount() < 1 ) fcSearch();

	// 1일차 경주별 분석 dataset copy
	dsOutRaceAnal.Filter("DAY_ORD='1'");
	dsOutRaceAnalPrint1.CopyF(dsOutRaceAnal);
	dsOutRaceAnal.UnFilter();
	// 2일차 경주별 분석 dataset copy
	dsOutRaceAnal.Filter("DAY_ORD='2'");
	dsOutRaceAnalPrint2.CopyF(dsOutRaceAnal);
	dsOutRaceAnal.UnFilter();
	// 3일차 경주별 분석 dataset copy
	dsOutRaceAnal.Filter("DAY_ORD='3'");
	dsOutRaceAnalPrint3.CopyF(dsOutRaceAnal);
	dsOutRaceAnal.UnFilter();
	// 출력
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";								//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "";								//레포트 파일명
	var sDataSet  = "Race=dsOutRace "
                  + "Voil=dsOutVoil "
                  + "VoilDesc=dsOutVoilDescPrint "
                  + "Payoff=dsOutPayoff "
                  + "RacePayoff=dsOutRacePayoff "
                  + "OrganAnal=dsOutOrganAnal "
                  + "RaceAnal1=dsOutRaceAnalPrint1 "
                  + "RaceAnal2=dsOutRaceAnalPrint2 "
                  + "RaceAnal3=dsOutRaceAnalPrint3 ";		//"UBI 쿼리명, MIP Dataset명"

	var sArg      = " IMG_URL="+logImgUrl
	              + " STND_YEAR="+cboSStndYear.Value
                  + " MBR_NM="+cboSMbr.Text
                  + " TMS="+cboSRaceTms.Value
                  + " RACE_CNT_1="+dsOutRace.GetColumn(0, "RACE_CNT_1")
                  + " RACE_CNT_2="+dsOutRace.GetColumn(0, "RACE_CNT_2")
                  + " RACE_CNT_3="+dsOutRace.GetColumn(0, "RACE_CNT_3");

	var nDayCnt = toInteger(dsOutRace.GetColumn(0, "DAY_CNT"));
	if ( nDayCnt < 3 )			sRptNm = "e02059020_organ_anal_2days.jrf";
	else if ( nDayCnt >= 3 )	sRptNm = "e02059020_organ_anal_3days.jrf";

	var sRptDrct  = 1;								//출력방향(0 : 수직, 1 : 수평)
	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}
