/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Index = 0;
        cboSMbr.Value = fce_GetUserInfo("MBR_CD");

        fce_GetRaceTms(cboSStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");
		return;
	}

	// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        cboSRaceTms.Index = 0;
        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
            if ( nIndexRow > -1 ) cboSRaceTms.Index = nIndexRow;
        }

        dsOutSRaceTms.row = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
        cboSRaceTms_OnChanged();
		return;
	}

	// 전체 선수 조회 후
	if ( sServiceName == "e02050030-service:searchRacerRecdAll" ) {
        //fnc_SetStatus(SNIS_BOA_0003, dsOutStndRaceCnt.GetRowCount());
		return;
	}

    // 성적상세조회 후
	if ( sServiceName == "e02050030-service:searchRacerRecd" ) {
        //fnc_SetStatus(SNIS_BOA_0003, dsOutStndRaceCnt.GetRowCount());
		return;
	}

	// 저장 후
	if ( sServiceName == "e02050030-service:searchRaceEnd" ) {
        if ( dsOutRaceEnd.GetRowCount() > 0 ) {
            btnSave.Enable = false;
            btnReturn.Enable = false;
        }

        fcSearch();
		return;
	}

	// 저장 후
	if ( sServiceName == "e02050030-service:saveRecd" ) {
        var nRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
        var nDayOrd = toInteger(dsOutSRaceTms.GetColumn(nRow, "DAY_ORD"));
        dsOutSRaceTms.SetColumn(nRow, "RACE_TMS_STAT_CD", "055");

        cboSRaceTms_OnChanged();
		return;
	}

	// 마감저장 후
	if ( sServiceName == "e02050030-service:saveRecdEnd" ) {
        var nRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
        var nDayOrd = toInteger(dsOutSRaceTms.GetColumn(nRow, "DAY_ORD"));
        dsOutSRaceTms.SetColumn(nRow, "RACE_TMS_STAT_CD", "060");

        cboSRaceTms_OnChanged();
		return;
	}

	// 마감취소저장 후
	if ( sServiceName == "e02050030-service:saveRecdCancel" ) {
        var nRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
        var nDayOrd = toInteger(dsOutSRaceTms.GetColumn(nRow, "DAY_ORD"));
        dsOutSRaceTms.SetColumn(nRow, "RACE_TMS_STAT_CD", "050");

        cboSRaceTms_OnChanged();
		return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

