/*-------------------------------------------------------------------------------------------------+
>>  회차변경시
+-------------------------------------------------------------------------------------------------*/
function cboSRaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fcInit();
    dsOutSRaceTms.row = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);

    var nTmsRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
    var sQurtCd = dsOutSRaceTms.GetColumn(nTmsRow, "QURT_CD_2");

    if ( sQurtCd == "001" ) {
        tbMain.tbRacer.grdRacerRecd.SetCellProp("body", 29, "ColId", "BF_F_CNT");
        tbMain.tbRacer.grdRacerRecd.SetCellProp("body", 30, "ColId", "BF_L_CNT");
    } else {
        tbMain.tbRacer.grdRacerRecd.SetCellProp("body", 29, "ColId", "AF_F_CNT");
        tbMain.tbRacer.grdRacerRecd.SetCellProp("body", 30, "ColId", "AF_L_CNT");
    }

    if ( dsOutSRaceTms.GetColumn(nTmsRow, "RACE_TMS_STAT_CD") == "060" ) {
        btnReturn.Enable         = true;
        btnSave.Enable = false;
        btnReturn.Text = "마감취소";
    } else {
        if ( dsOutSRaceTms.GetColumn(nTmsRow, "RACE_TMS_STAT_CD") >= "040" ) {
            btnSave.Enable = true;
            btnReturn.Enable         = true;
        } else {
            btnSave.Enable = false;
            btnReturn.Enable         = false;
        }
        btnReturn.Text = "마    감";
    }

    fcSearchRaceEnd();
    // var nRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
    // var nDayOrd = toInteger(dsOutSRaceTms.GetColumn(nRow, "DAY_ORD"));

    // for ( var i = 0; i < nDayOrd; i++ ) {
        // tbMain.tbMot.grdMotRecd.SetColProp(1 + i, "Width", 50);
    // }
    // for ( var i = nDayOrd; i <= 7; i++ ) {
        // tbMain.tbMot.grdMotRecd.SetColProp(1 + i, "Width",  0);
    // }


    //모터탭 착수 조절.
    setMotGridSize();
    //보트탭 착수 조절.
    setBoatGridSize();

    btnSave.Enable = true;	// ===> test by chojob
}

/*-------------------------------------------------------------------------------------------------+
>>  선수간략정보 Grid에 보여줌
+-------------------------------------------------------------------------------------------------*/
function dsOutRacerRecd_OnRowPosChanged(obj,nOldRow,nRow)
{
    fnc_SetAllColumnValue(dsOutRacerSum, "DESC", "");

    var sTms6AmuRankScr  = toNumber(obj.GetColumn(nRow, "TMS_6_AMU_RANK_SCR"));
    var sTms6AmuAcdntScr = toNumber(obj.GetColumn(nRow, "TMS_6_AMU_ACDNT_SCR"));
    var sTms6RaceCnt     = toNumber(obj.GetColumn(nRow, "TMS_6_RACE_CNT"));
    var sTms6Rank1       = toNumber(obj.GetColumn(nRow, "TMS_6_RANK_1"));
    var sTms6Rank2       = toNumber(obj.GetColumn(nRow, "TMS_6_RANK_2"));
    var sTms6Rank3       = toNumber(obj.GetColumn(nRow, "TMS_6_RANK_3"));

    var sAmuRankScr  = toNumber(obj.GetColumn(nRow, "AMU_RANK_SCR"));
    var sAmuAcdntScr = toNumber(obj.GetColumn(nRow, "AMU_ACDNT_SCR"));
    var sRaceCnt     = toNumber(obj.GetColumn(nRow, "RACE_CNT"));
    var sRank1       = toNumber(obj.GetColumn(nRow, "RANK_1_CNT"));
    var sRank2       = toNumber(obj.GetColumn(nRow, "RANK_2_CNT"));
    var sRank3       = toNumber(obj.GetColumn(nRow, "RANK_3_CNT"));

    var i = 0;
    var sTemp = "";

    // 6회차
    if ( sTms6RaceCnt > 0 ) {
        // 6회차 착순점
        sTemp = sTms6AmuRankScr 
              + " / " 
              + sTms6RaceCnt 
              + " = " 
              + round(sTms6AmuRankScr / sTms6RaceCnt, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

        // 6회차 득점
        sTemp = "( "
              + sTms6AmuRankScr 
              + " - "
              + sTms6AmuAcdntScr 
              + " ) / " 
              + sTms6RaceCnt 
              + " = " 
              + round((sTms6AmuRankScr - sTms6AmuAcdntScr) / sTms6RaceCnt, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

        // 6회차 승률
        sTemp = sTms6Rank1 
              + " / " 
              + sTms6RaceCnt 
              + " × " 
              + 100 
              + " = " 
              + round(sTms6Rank1 / sTms6RaceCnt * 100, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

        // 6회차 연대율
        sTemp = "( "
              + sTms6Rank1 
              + " + " 
              + sTms6Rank2 
              + " ) / " 
              + sTms6RaceCnt 
              + " × " 
              + 100 
              + " = " 
              + round((sTms6Rank1 + sTms6Rank2) / sTms6RaceCnt * 100, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

        // 6회차 3연대율
        sTemp = "( "
              + sTms6Rank1 
              + " + " 
              + sTms6Rank2 
              + " + " 
              + sTms6Rank3 
              + " ) / " 
              + sTms6RaceCnt 
              + " × " 
              + 100 
              + " = " 
              + round((sTms6Rank1 + sTms6Rank2 + sTms6Rank3) / sTms6RaceCnt * 100, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);
    }

    i = 5;
    // 년도별
    if ( sRaceCnt > 0 ) {
        // 착순점
        sTemp = sAmuRankScr 
              + " / " 
              + sRaceCnt 
              + " = " 
              + round(sAmuRankScr / sRaceCnt, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

        // 6회차 연대율
        sTemp = "( "
              + sRank1 
              + " + " 
              + sRank2 
              + " ) / " 
              + sRaceCnt 
              + " × " 
              + 100 
              + " = " 
              + round((sRank1 + sRank2) / sRaceCnt * 100, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

        // 6회차 3연대율
        sTemp = "( "
              + sRank1 
              + " + " 
              + sRank2 
              + " + " 
              + sRank3 
              + " ) / " 
              + sRaceCnt 
              + " × " 
              + 100 
              + " = " 
              + round((sRank1 + sRank2 + sRank3) / sRaceCnt * 100, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

        // 반기사고점
        var nTmsRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
        var sQurtCd = dsOutSRaceTms.GetColumn(nTmsRow, "QURT_CD_2");

        if ( sQurtCd == "001" ) {
            var sBfQurtAcdntScr = toNumber(obj.GetColumn(nRow, "BF_AMU_ACDNT_SCR"));
            var sBfRaceCnt      = toNumber(obj.GetColumn(nRow, "BF_RACE_CNT"));
            sTemp = sBfQurtAcdntScr 
                  + " / " 
                  + sBfRaceCnt 
                  + " = " 
                  + round(sBfQurtAcdntScr / sBfRaceCnt, 4);
            dsOutRacerSum.SetColumn(i++, "DESC", sTemp);
        } else {
            var sAfQurtAcdntScr = toNumber(obj.GetColumn(nRow, "AF_AMU_ACDNT_SCR"));
            var sAfRaceCnt      = toNumber(obj.GetColumn(nRow, "AF_RACE_CNT"));
            sTemp = sAfQurtAcdntScr 
                  + " / " 
                  + sAfRaceCnt 
                  + " = " 
                  + round(sAfQurtAcdntScr / sAfRaceCnt, 4);
            dsOutRacerSum.SetColumn(i++, "DESC", sTemp);
        }
    }

	var sStMthdCd = "000"; 
	if(IndexOf(obj.id, "Fly") > 0) sStMthdCd = "002"; 
	if(IndexOf(obj.id, "Onl") > 0) sStMthdCd = "001"; 

	fcSearchRecd("RACER", sStMthdCd);
}

