    return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  년도별선수정보초기화 저장
+-------------------------------------------------------------------------------------------------*/
function fcRacerRecdInitSave(obj) {
    if ( !fcRacerRecdInitSaveCheck(obj) ) return;

	var sServiceName = "e02050030-service:saveRacerRecdInit";
    var sInDataSet   = " ";
    var sOutDataSet  = " ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  성적생성 버튼 클릭시
+-------------------------------------------------------------------------------------------------*/
function btnSearchRecd_OnClick(obj)
{
    fnc_CopyDataset(dsInRacer, dsOutRacerRecd, "chk", 1);

    if ( dsInRacer.GetRowCount() == 0 ) {
		fnc_Message(SNIS_COM_1006, "N");
        return false;
    }

	fcInit();

	var sServiceName = "e02050030-service:searchRecdCreate";
    var sInDataSet   = "dsOutRacerRecd=dsInRacer ";
    var sOutDataSet  = "dsOutRacerRecd=dsOutRacerRecd "
                     + "dsOutRacerRecdFly=dsOutRacerRecdFly "
                     + "dsOutRacerRecdOnl=dsOutRacerRecdOnl "
                     + "dsOutMotRecd=dsOutMotRecd "
                     + "dsOutMotRecdFly=dsOutMotRecdFly "
                     + "dsOutMotRecdOnl=dsOutMotRecdOnl "
                     + "dsOutBoatRecd=dsOutBoatRecd "
                     + "dsOutBoatRecdFly=dsOutBoatRecdFly "
                     + "dsOutBoatRecdOnl=dsOutBoatRecdOnl ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
    fce_AddParam(Trim("ALL_YN   "), chkAllYn.Value    );

 	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");	
}

