/*-------------------------------------------------------------------------------------------------+
>>  회차별 최고정번수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function setMotGridSize(){
	/*
		IWORK_SFR-008 경정 선수편성 메뉴 개선  정번8 까지 추가 수정.
		회차에 따른 최고(8까지)정번수 별로 1 ~ 정번수 까지 화면에 표시.
	*/
	var nMaxTmsRunCnt = 8;
	var nMaxRaceRegNoCnt = dsOutSRaceTms.GetColumn(cboSRaceTms.Index, "MAX_RACE_REG_NO_CNT");

	//Alert("nMaxRaceRegNoCnt : " + nMaxRaceRegNoCnt);

	tbMain.tbMot.grdMotRecd.Redraw = false;

	var nCol0W = tbMain.tbMot.grdMotRecd.GetColProp(0, "Width");
	var nCol1W = tbMain.tbMot.grdMotRecd.GetColProp(1, "Width");
	var nCol2W = tbMain.tbMot.grdMotRecd.GetColProp(2, "Width");
	var nCol3W = tbMain.tbMot.grdMotRecd.GetColProp(3, "Width");

	//전체적으로 사이즈를 넓힘.
	for ( var i = 0; i < nMaxTmsRunCnt; i++ ) {
        tbMain.tbMot.grdMotRecd.SetColProp(4 + i, "Width", 30);
    }

    //nMaxTmsRunCnt 부터 nMaxRaceRegNoCnt까지 사이즈를 줄임.
    for ( var i = nMaxTmsRunCnt; i > nMaxRaceRegNoCnt; i-- ) {
        tbMain.tbMot.grdMotRecd.SetColProp(4 + i-1, "Width", 0);
    }

    tbMain.tbMot.grdMotRecd.SetColProp(0, "Width", nCol0W);
    tbMain.tbMot.grdMotRecd.SetColProp(1, "Width", nCol1W);
    tbMain.tbMot.grdMotRecd.SetColProp(2, "Width", nCol2W);
    tbMain.tbMot.grdMotRecd.SetColProp(3, "Width", nCol3W);

    tbMain.tbMot.grdMotRecd.Redraw = true;
}


/*-------------------------------------------------------------------------------------------------+
>>  회차별 최고정번수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function setBoatGridSize(){
	/*
		IWORK_SFR-008 경정 선수편성 메뉴 개선  정번8 까지 추가 수정.
		회차에 따른 최고(8까지)정번수 별로 1 ~ 정번수 까지 화면에 표시.
	*/
	var nMaxTmsRunCnt = 8;
	var nMaxRaceRegNoCnt = dsOutSRaceTms.GetColumn(cboSRaceTms.Index, "MAX_RACE_REG_NO_CNT");

	//Alert("nMaxRaceRegNoCnt : " + nMaxRaceRegNoCnt);

	tbMain.tbBoat.grdBoatRecd.Redraw = false;

	var nCol0W = tbMain.tbBoat.grdBoatRecd.GetColProp(0, "Width");
	var nCol1W = tbMain.tbBoat.grdBoatRecd.GetColProp(1, "Width");
	var nCol2W = tbMain.tbBoat.grdBoatRecd.GetColProp(2, "Width");
	var nCol3W = tbMain.tbBoat.grdBoatRecd.GetColProp(3, "Width");

	//전체적으로 사이즈를 넓힘.
	for ( var i = 0; i < nMaxTmsRunCnt; i++ ) {
        tbMain.tbBoat.grdBoatRecd.SetColProp(4 + i, "Width", 40);
    }

    //nMaxTmsRunCnt 부터 nMaxRaceRegNoCnt까지 사이즈를 줄임.
    for ( var i = nMaxTmsRunCnt; i > nMaxRaceRegNoCnt; i-- ) {
        tbMain.tbBoat.grdBoatRecd.SetColProp(4 + i-1, "Width", 0);
    }

    tbMain.tbBoat.grdBoatRecd.SetColProp(0, "Width", nCol0W);
    tbMain.tbBoat.grdBoatRecd.SetColProp(1, "Width", nCol1W);
    tbMain.tbBoat.grdBoatRecd.SetColProp(2, "Width", nCol2W);
    tbMain.tbBoat.grdBoatRecd.SetColProp(3, "Width", nCol3W);

    tbMain.tbBoat.grdBoatRecd.Redraw = true;
}
function fcFormOnFocus(obj)
{
	if (gdsParam.rowcount() > 0) {
		fcSetParam();
		fcSearch();
	}
}

function fcSetParam()
{
	if (gdsParam.rowcount() > 0) {
		btnRacerRecdInit.Visible = false;
		btnSearchRecd.Visible = false;
		btnReturn.Visible = false;
		var sMotNo  = gdsParam.GetColumn(0, "MOT_NO");
		var sBoatNo = gdsParam.GetColumn(0, "BOAT_NO");
		if (!fnc_isNull(sMotNo)) {
			tbMain.TabIndex = 1;
			tbMain.tbRacer.edRacerNm.text = "-";
			tbmain.tbMot.edMotNo.Text = sMotNo;
			tbmain.tbBoat.edBoatNo.text = "-";
		} else if (!fnc_isNull(sBoatNo)) {
			tbMain.TabIndex = 2;
			tbMain.tbRacer.edRacerNm.text = "-";
			tbmain.tbBoat.edBoatNo.text = sBoatNo;
			tbmain.tbMot.edMotNo.Text = "-";
		}	
		gdsParam.clear();
	}
}
