/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
		fce_AddCodeRow(dsOutSTrngRsn    , "전체", "", cboSTrngRsn     );
		fce_AddCodeRow(dsOutTrngExptStat, "전체", "", cboSTrngExptStat);
		cboTrngRsn.Index = 0;
		fcSearch();
		return;
	}

	// 조회 후
	if ( sServiceName == "e02070020-service:searchRacerSanc" ) {
        if ( dsOutRacerSanc.GetRowCount() > 0 ) {

			if (!fnc_isNull(dsOutRacerSanc.GetColumn(0, "TRNG_EXPT_SEQ"))) {
				if (fnc_Message("이미 훈련예정선수로 등록되어 있습니다.\n추가로 등록하시겠습니까?\n(미이수 선수에 한함)", "Y") == false)
					return;
			}
            var nRow = dsOutTrngExptRacer.currow;
            //fnc_DatasetPrint(dsOutRacerSanc);

            dsOutTrngExptRacer.SetColumn(nRow, Trim("SANC_ISSUE_NO    "), dsOutRacerSanc.GetColumn(0, Trim("SANC_ISSUE_NO    ")));
            dsOutTrngExptRacer.SetColumn(nRow, Trim("SANC_BASIS_CD    "), dsOutRacerSanc.GetColumn(0, Trim("SANC_BASIS_CD    ")));
            dsOutTrngExptRacer.SetColumn(nRow, Trim("SANC_TERM        "), dsOutRacerSanc.GetColumn(0, Trim("SANC_TERM        ")));
            dsOutTrngExptRacer.SetColumn(nRow, Trim("SANC_RSN_CD      "), dsOutRacerSanc.GetColumn(0, Trim("SANC_RSN_CD      ")));
            dsOutTrngExptRacer.SetColumn(nRow, Trim("SANC_RSN         "), dsOutRacerSanc.GetColumn(0, Trim("SANC_RSN         ")));
            dsOutTrngExptRacer.SetColumn(nRow, Trim("SANC_ACT_DT      "), dsOutRacerSanc.GetColumn(0, Trim("SANC_ACT_DT      ")));
            dsOutTrngExptRacer.SetColumn(nRow, Trim("SANC_OBJECTION   "), dsOutRacerSanc.GetColumn(0, Trim("SANC_OBJECTION   ")));
            dsOutTrngExptRacer.SetColumn(nRow, Trim("UNIT             "), dsOutRacerSanc.GetColumn(0, Trim("UNIT             ")));
            dsOutTrngExptRacer.SetColumn(nRow, Trim("SMRY             "), dsOutRacerSanc.GetColumn(0, Trim("SMRY             ")));
        }
		return;
	}

	// 조회 후
	if ( sServiceName == "e02070020-service:searchTrngExptRacer" ) {
        //dsOutTrngExptRacer.AddColumn("chk");
        //Trace(dsOutTrngExptRacer.savexml());
		return;
	}

	// 저장 후
	if ( sServiceName == "e02070020-service:saveTrngExptRacer" ) {
        dsOutTrngExptRacer.AddColumn("chk");
		return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {
	switch(sServiceName) {
		case "common_help-service:searchRacer" :
			//코드명 조회 후 처리
			fce_MultiRowDisplay(this, vREVOBJ, sGRDTIT, sCUROBJ);
			var oFocusComponent = GetNextComponent(true);
			oFocusComponent.setfocus();
			break;
		case "common_help-service:searchRacerSanc" :
			//코드명 조회 후 처리
			var sReturn = fce_MultiRowDisplay(this, vREVOBJ, sGRDTIT, sCUROBJ);
			var oFocusComponent = GetNextComponent(true);
			oFocusComponent.setfocus();

			if ( sReturn ) fcGetRacerSanc();

			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  코드나 명 입력 후 Lost Focus 시 해당 정보 Display
+-------------------------------------------------------------------------------------------------*/
function fcCodeNameDisplay(obj) {
	//변화가 없으면 굳이 실행 하지 않는다.
	//if (length(obj.userdata) > 0 && obj.value == obj.userdata ) return;

	var sInputValue  = trim(obj.value);      //검색 할 자료 

	var sServiceName = "";                   //서버에서 호출 될 Service 명
	var sActionName  = "";                   //서버에서 호출 될 Action 명
	var sInDataSet   = "";                   //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet  = "";                   //조회 후 돌려 받을 DataSet 명칭   

	// 초기화
	sCUROBJ  = object(obj.id);
	sGRDTIT  = "";
	switch(obj.id) {
		case "edtRacerNo":                   	            //선수 등록번호
		case "edtNmKor"  :                   	            //선수명
			vREVOBJ[0]   = "edtRacerNo";        			//선수 등록번호를 받을 Text Object명
			vREVOBJ[1]   = "edtNmKor";        				//성명을 받을 Text Object명
			vREVOBJ[2]   = "edtRacerGrd";      				//성명을 받을 Text Object명
			sGRDTIT      = "등록번호:성명:등급";            //그리드 제목

			sServiceName = "common_help-service:searchRacer";
			sInDataSet   = "";                      		//자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=dsOutRacer";		//조회 후 돌려 받을 DataSet 명칭  

			//fce_AddParam(Trim("RACER_NO"), Trim(edtRacerNo.Text));
			fce_AddParam(Trim("NM_KOR  "), Trim(edtNmKor.Text  ));
			break;
		case "edtSancIssueNo" :								// 제재번호
			if ( fnc_IsNull(edtRacerNo.Text) ) return;
			if ( cboTrngRsn.Value != "001"   ) return;
			if ( edtSancIssueNo.Readonly     ) return;

			vREVOBJ[0]   = "edtSancIssueNo";					//부서 코드를 받을 Text Object명
			vREVOBJ[1]   = "";    					//관련 근거를 받을 Text Object명
			vREVOBJ[2]   = "";        				//제재처분기간을 받을 Text Object명
//			vREVOBJ[3]   = "calSancActDt";        				//단위를 받을 Text Object명
			sGRDTIT      = "발급번호:제재처분:사유:처분일";	    //그리드 제목

			sServiceName = "common_help-service:searchRacerSanc";
			sInDataSet   = "";								//자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=dsOutRacerSanc";	//조회 후 돌려 받을 DataSet 명칭  

			fce_AddParam(Trim("RACER_NO     "), Trim(edtRacerNo.Text));
			fce_AddParam(Trim("SANC_ISSUE_NO"), Trim(edtSancIssueNo.Text));
			edtRacerNo.SetFocus();
			break;
		default:
			return;
	}

	//조회 서비스 실행
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  제재번호에 따른 상세내용조회
+-------------------------------------------------------------------------------------------------*/
function fcGetRacerSanc() {

	var sServiceName = "e02070020-service:searchRacerSanc";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerSanc=dsOutRacerSanc ";

	fce_AddParam(Trim("SANC_ISSUE_NO"), edtSancIssueNo.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  훈련사유에 따른 화면 설정
+-------------------------------------------------------------------------------------------------*/
function cboTrngRsn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if ( cboTrngRsn.Value == "001" ) {
        edtSancIssueNo.Readonly = false;
	} else {
        edtSancIssueNo.Readonly = true;
	}

	fnc_SetBKColor(this);
}

/*-------------------------------------------------------------------------------------------------+
>>  선택된 예정선수에 따른 화면설정
+-------------------------------------------------------------------------------------------------*/
function dsOutTrngExptRacer_OnRowPosChanged(obj,nOldRow,nRow)
{
	if (dsOutTrngExptRacer.GetColumn(nRow, "TRNG_RSN_CD") == "003") 
		fcChangeTitle("INJURY");
	else
		fcChangeTitle("SANC");

	if (toUpper(dsOutTrngExptRacer.GetRowType(nRow)) == "INSERT") {
		edtNmKor.Readonly = false;
	} else {
		edtNmKor.Readonly = true;
	}
	fnc_SetBKColor(this);
}

function fcBtnOnClick(obj)
{
	switch(obj.id) {
		case "btnPopCandi":                   	            //선수 등록번호
			var sFormId 	= "form::E02070021.xml";
			var sArgs 		= "";		
			var nCXScreen	= GetDeviceInfo("CXScreen");			
			var nCYScreen	= GetDeviceInfo("CYScreen");
			var sSancIssueNo= "";
			var sInjurySeq  = "";
			var sStndYear  = "";
			var sRacerNo  = "";
			var sSearchStr  = "";

			var bRtn = Dialog(sFormId, "",nCXScreen,nCYScreen, "Title=true CloseFlag=true","1","1");
			if (bRtn) {
				var nRow = 0;
				for(var i=0;i<gdsReturn.RowCount();i++) {

					// 제재인 경우	
					if (gdsReturn.GetColumn(i, "TRNG_RSN_CD") == "001" || 
					    gdsReturn.GetColumn(i, "TRNG_RSN_CD") == "002" || 
					    gdsReturn.GetColumn(i, "TRNG_RSN_CD") == "005") { // 주선보류훈련(6개월출전정지:8주훈련)						
						// 동일 선수가 등록되었으면 제외
						sSancIssueNo = gdsReturn.GetColumn(i, "SANC_ISSUE_NO");
						if (dsOutTrngExptRacer.FindRow("SANC_ISSUE_NO", sSancIssueNo) >= 0) continue;					

						nRow = dsOutTrngExptRacer.AddRow();
						dsOutTrngExptRacer.SetColumn(nRow, "RACER_NO", 			gdsReturn.GetColumn(i, "RACER_NO"));
						dsOutTrngExptRacer.SetColumn(nRow, "NM_KOR", 			gdsReturn.GetColumn(i, "NM_KOR"));
						dsOutTrngExptRacer.SetColumn(nRow, "RACER_GRD_CD", 		gdsReturn.GetColumn(i, "RACER_GRD_CD"));
						dsOutTrngExptRacer.SetColumn(nRow, "TRNG_RSN_CD", 		gdsReturn.GetColumn(i, "TRNG_RSN_CD"));
						dsOutTrngExptRacer.SetColumn(nRow, "TRNG_RQST_DD_NUM", 	gdsReturn.GetColumn(i, "TRNG_RQST_DD_NUM"));
						dsOutTrngExptRacer.SetColumn(nRow, "SANC_ISSUE_NO", 	gdsReturn.GetColumn(i, "SANC_ISSUE_NO"));
						dsOutTrngExptRacer.SetColumn(nRow, "SANC_BASIS_CD", 	gdsReturn.GetColumn(i, "SANC_BASIS_CD"));
						dsOutTrngExptRacer.SetColumn(nRow, "SANC_ACT_DT", 		gdsReturn.GetColumn(i, "SANC_ACT_DT"));
						dsOutTrngExptRacer.SetColumn(nRow, "SANC_TERM", 		gdsReturn.GetColumn(i, "SANC_TERM"));
						dsOutTrngExptRacer.SetColumn(nRow, "SANC_TERM_UNIT", 	gdsReturn.GetColumn(i, "SANC_TERM_UNIT"));
						dsOutTrngExptRacer.SetColumn(nRow, "SANC_RSN_CD", 		gdsReturn.GetColumn(i, "SANC_RSN_CD"));
						dsOutTrngExptRacer.SetColumn(nRow, "SANC_RSN", 			gdsReturn.GetColumn(i, "SANC_RSN"));
						dsOutTrngExptRacer.SetColumn(nRow, "SMRY", 				gdsReturn.GetColumn(i, "SMRY"));
						dsOutTrngExptRacer.SetColumn(nRow, "RMK", 				gdsReturn.GetColumn(i, "RMK"));
						dsOutTrngExptRacer.SetColumn(nRow, "SANC_TERM_UNIT_NM", 	gdsReturn.GetColumn(i, "SANC_TERM_UNIT_NM"));
						dsOutTrngExptRacer.SetColumn(nRow, "STND_YEAR", 		gdsReturn.GetColumn(i, "STND_YEAR"));
						dsOutTrngExptRacer.SetColumn(nRow, "INST_DTM", 			fce_GetSysdate("YYYYMMDD"));					
						dsOutTrngExptRacer.SetColumn(nRow, "TRNG_RQST_SEQ", 	gdsReturn.GetColumn(i, "TRNG_RQST_SEQ"));
						fcChangeTitle("SANC");
					} else if (gdsReturn.GetColumn(i, "TRNG_RSN_CD") == "003") { // 부상훈련
						// 동일 선수가 등록되었으면 제외
						sInjurySeq = gdsReturn.GetColumn(i, "SEQ");
						sStndYear  = gdsReturn.GetColumn(i, "STND_YEAR");
						sRacerNo   = gdsReturn.GetColumn(i, "RACER_NO");
						sSearchStr = "INJURY_SEQ='"+sInjurySeq+"' && STND_YEAR='"+sStndYear+"' && RACER_NO='"+sRacerNo+"'";
						if (dsOutTrngExptRacer.SearchRow(sSearchStr) >= 0) {
							fnc_message(gdsReturn.GetColumn(i, "NM_KOR")+" 선수는 이미 등록되었습니다.", "N");
							continue;					
						}

						nRow = dsOutTrngExptRacer.AddRow();
						dsOutTrngExptRacer.SetColumn(nRow, "INJURY_SEQ", 		gdsReturn.GetColumn(i, "SEQ"));
						dsOutTrngExptRacer.SetColumn(nRow, "RACER_NO", 			gdsReturn.GetColumn(i, "RACER_NO"));
						dsOutTrngExptRacer.SetColumn(nRow, "NM_KOR", 			gdsReturn.GetColumn(i, "NM_KOR"));
						dsOutTrngExptRacer.SetColumn(nRow, "RACER_GRD_CD", 		gdsReturn.GetColumn(i, "RACER_GRD_CD"));
						dsOutTrngExptRacer.SetColumn(nRow, "TRNG_RSN_CD", 		gdsReturn.GetColumn(i, "TRNG_RSN_CD"));
						dsOutTrngExptRacer.SetColumn(nRow, "TRNG_RQST_DD_NUM", 	gdsReturn.GetColumn(i, "TRNG_RQST_DD_NUM"));

						dsOutTrngExptRacer.SetColumn(nRow, "SANC_TERM", 	  	gdsReturn.GetColumn(i, "DIAGNS_WK"));	// 부상기간
						dsOutTrngExptRacer.SetColumn(nRow, "SANC_RSN", 			gdsReturn.GetColumn(i, "IJRY_STAT"));	// 부상부위
						dsOutTrngExptRacer.SetColumn(nRow, "SANC_ISSUE_NO", 	gdsReturn.GetColumn(i, "STND_YEAR")+"-"+gdsReturn.GetColumn(i, "SEQ"));
						dsOutTrngExptRacer.SetColumn(nRow, "SANC_TERM_UNIT_NM", "주");								// 부상기간 단위(주)
						dsOutTrngExptRacer.SetColumn(nRow, "SANC_ACT_DT", 		gdsReturn.GetColumn(i, "IJRY_DT"));	// 부상일자
						dsOutTrngExptRacer.SetColumn(nRow, "STND_YEAR", 		gdsReturn.GetColumn(i, "STND_YEAR"));
						dsOutTrngExptRacer.SetColumn(nRow, "INST_DTM", 			fce_GetSysdate("YYYYMMDD"));					
						dsOutTrngExptRacer.SetColumn(nRow, "TRNG_RQST_SEQ", 	gdsReturn.GetColumn(i, "TRNG_RQST_SEQ"));
						fcChangeTitle("INJURY");
					} else if (gdsReturn.GetColumn(i, "TRNG_RSN_CD") == "004") {	//희망훈련
						// 동일 선수가 등록되었으면 제외
						sInjurySeq = gdsReturn.GetColumn(i, "SEQ");
						sStndYear  = gdsReturn.GetColumn(i, "STND_YEAR");
						sRacerNo   = gdsReturn.GetColumn(i, "RACER_NO");
						sSearchStr = "RACER_NO='"+sRacerNo+"'";
						if (dsOutTrngExptRacer.SearchRow(sSearchStr) >= 0) {
							fnc_message(gdsReturn.GetColumn(i, "NM_KOR")+" 선수는 이미 등록되었습니다.", "N");
							continue;					
						}

						nRow = dsOutTrngExptRacer.AddRow();
						dsOutTrngExptRacer.SetColumn(nRow, "RACER_NO", 			gdsReturn.GetColumn(i, "RACER_NO"));
						dsOutTrngExptRacer.SetColumn(nRow, "NM_KOR", 			gdsReturn.GetColumn(i, "NM_KOR"));
						dsOutTrngExptRacer.SetColumn(nRow, "RACER_GRD_CD", 		gdsReturn.GetColumn(i, "RACER_GRD_CD"));
						dsOutTrngExptRacer.SetColumn(nRow, "TRNG_RSN_CD", 		gdsReturn.GetColumn(i, "TRNG_RSN_CD"));
						dsOutTrngExptRacer.SetColumn(nRow, "TRNG_RQST_DD_NUM", 	gdsReturn.GetColumn(i, "TRNG_RQST_DD_NUM"));

						dsOutTrngExptRacer.SetColumn(nRow, "STND_YEAR", 		gdsReturn.GetColumn(i, "STND_YEAR"));
						dsOutTrngExptRacer.SetColumn(nRow, "INST_DTM", 			fce_GetSysdate("YYYYMMDD"));

						dsOutTrngExptRacer.SetColumn(nRow, "TRNG_RQST_SEQ", 	gdsReturn.GetColumn(i, "TRNG_RQST_SEQ"));

						//비고에 훈련 시작/종료일을 표시
						if(!fnc_IsNull(gdsReturn.GetColumn(i, "IJRY_SDT")) && !fnc_IsNull(gdsReturn.GetColumn(i, "IJRY_EDT"))){
							dsOutTrngExptRacer.SetColumn(nRow, "RMK", 			gdsReturn.GetColumn(i, "IJRY_SDT") + " ~ " + gdsReturn.GetColumn(i, "IJRY_EDT"));
						}	 

						//휴대폰 번호 (승인 시 SMS 보내야 함)
						dsOutTrngExptRacer.SetColumn(nRow, "CELPON_NO", 	gdsReturn.GetColumn(i, "CELPON_NO"));

						//진행상태 (SMS 보내야할지 여부 판단)
						dsOutTrngExptRacer.SetColumn(nRow, "ACCEPT_STAT", 	gdsReturn.GetColumn(i, "ACCEPT_STAT"));

						fcChangeTitle("");
					} else if (gdsReturn.GetColumn(i, "TRNG_RSN_CD") == "005") {	
						// 동일 선수가 등록되었으면 제외
						sInjurySeq = gdsReturn.GetColumn(i, "SEQ");
						sStndYear  = gdsReturn.GetColumn(i, "STND_YEAR");
						sRacerNo   = gdsReturn.GetColumn(i, "RACER_NO");
						sSearchStr = "RACER_NO='"+sRacerNo+"'";
						if (dsOutTrngExptRacer.SearchRow(sSearchStr) >= 0) {
							fnc_message(gdsReturn.GetColumn(i, "NM_KOR")+" 선수는 이미 등록되었습니다.", "N");
							continue;					
						}

						nRow = dsOutTrngExptRacer.AddRow();
						dsOutTrngExptRacer.SetColumn(nRow, "RACER_NO", 			gdsReturn.GetColumn(i, "RACER_NO"));
						dsOutTrngExptRacer.SetColumn(nRow, "NM_KOR", 			gdsReturn.GetColumn(i, "NM_KOR"));
						dsOutTrngExptRacer.SetColumn(nRow, "RACER_GRD_CD", 		gdsReturn.GetColumn(i, "RACER_GRD_CD"));
						dsOutTrngExptRacer.SetColumn(nRow, "TRNG_RSN_CD", 		gdsReturn.GetColumn(i, "TRNG_RSN_CD"));
						dsOutTrngExptRacer.SetColumn(nRow, "TRNG_RQST_DD_NUM", 	gdsReturn.GetColumn(i, "TRNG_RQST_DD_NUM"));

						dsOutTrngExptRacer.SetColumn(nRow, "STND_YEAR", 		gdsReturn.GetColumn(i, "STND_YEAR"));
						dsOutTrngExptRacer.SetColumn(nRow, "INST_DTM", 			fce_GetSysdate("YYYYMMDD"));					
						fcChangeTitle("");
					}
				}
			}

			break;
	}
}
function fcChangeTitle(sType)
{
	if (sType == "INJURY") {
		stIssueNo.text = "부상일련번호";
		stSancRsn.text = "부상부위";
		stIssueDt.text = "부상일자";
		stSancTerm.text = "부상진단기간";
	} else {
		stIssueNo.text = "제재번호";
		stSancRsn.text = "제재사유";
		stIssueDt.text = "처분일자";
		stSancTerm.text = "제재처분기간";
	}
}


function fcOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar==13) {
		fcCodeNameDisplay(obj);
	}
}

function fcOnMouseOver(obj,nX,nY)
{
	if (obj.id == "edtNmKor") {
		if (fnc_IsNull(edtNmKor.Text)) {
			edtRacerNo.text = "";
			edtRacerGrd.text = "";
		}
	}
}
