>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	sBUTTONLIST   = 'F' + sInpt + 'FFFF';
	//fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	//공통코드 조회
	fce_GetComboDs(dsInComCd);

	// 화면 초기화
	fcInit();

	tab0.tab1.calEndDt.Value = fce_GetSysdate("YYYYMMDD");
	tab0.tab1.calStrDt.Value = Mid(tab0.tab1.calEndDt.Value, 0, 4) + "0101";

	tab0.tab3.calStrDt.Value = fce_GetSysdate("YYYYMMDD");
	tab0.tab3.calEndDt.Value =  AddMonth(calEndDt.Value, 2);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

    //년도조회
    fce_SetStndYear(dsOutSStndYear, toInteger(fce_GetSysdate("YYYY")));
    tab0.tab2.cboSStndYear.Index = 0;

	// 현재 회차 조회
    fce_GetCurTms();    

		// 조회
	fcSearch();


}

