/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

    if ( !fnc_IsMandatory(tab0.tab1.calStrDt, "시작일") ) return false;
    if ( !fnc_IsMandatory(tab0.tab1.calEndDt, "종료일") ) return false;

	if ( tab0.tab1.calStrDt.Value > tab0.tab1.calEndDt.Value ) {
        fnc_Message(SNIS_BOA_B011, "N", "시작일", "종료일");
        tab0.tab1.calStrDt.setFocus();
        return false;
	}

	fcInit(obj);

	return true;
}

function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	if (tab0.tabindex == 0) {

		var sServiceName = "e02070021-service:searchTrngExptRacerCandi";
		var sInDataSet   = "";
		var sOutDataSet  = "dsOutTrngExptRacer=dsOutTrngExptRacer ";

		fce_AddParam(Trim("NM_KOR           "), edRacerNm.Value       );
		fce_AddParam(Trim("TRNG_EXPT_STAT_CD"), cboSTrngExptStat.Value);
		fce_AddParam(Trim("STR_DT           "), tab0.tab1.calStrDt.Value        );
		fce_AddParam(Trim("END_DT           "), tab0.tab1.calEndDt.Value        );
		fce_AddParam(Trim("NOT_YET          "), tab0.tab1.chkNotYet.Value       );

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

	} else if (tab0.tabindex == 1) {

		var sServiceName = "e02070021-service:searchRacerIjryCandi";
		var sInDataSet   = "";
		var sOutDataSet  = "dsOutRacerIjry=dsOutRacerIjry  "
						 + "dsOutRacerSanc=dsOutRacerSanc  ";
		fce_AddParam(Trim("STND_YEAR"), tab0.tab2.cboSStndYear.value);
		fce_AddParam(Trim("NM_KOR"), 	tab0.tab2.edRacerNm.Text);
		fce_AddParam(Trim("NOT_YET"), 	tab0.tab2.chkNotYet.Value);

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	} else if (tab0.tabindex == 2) {	// 희망훈련선수:전체선수 조회

		var sServiceName = "e02070021-service:searchRacerWantCandi";
		var sInDataSet   = "";
		var sOutDataSet  = "dsOutRacerWant=dsOutRacerWant ";
		sOutDataSet  	 = sOutDataSet + "dsTrngRqst=dsTrngRqst ";
		fce_AddParam(Trim("RACER_NO"), tab0.tab3.edRacerNo.text);
		fce_AddParam(Trim("NM_KOR"), 	tab0.tab3.edRacerNm.Text);
		fce_AddParam(Trim("TRNG_STRT_DT"), 	tab0.tab3.calStrDt.value);
		fce_AddParam(Trim("TRNG_END_DT"), 	tab0.tab3.calEndDt.value);	

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutTrngExptRacer) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutTrngExptRacer;
	sMandatoryColID   = "RACER_NO|TRNG_RSN_CD|TRNG_RQST_DD_NUM";
	sMandatoryColName = "등록번호|훈련사유|훈련요구일";
	oGrid             = grdTrngExptRacer;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

    // 원본 DataSet을 Loop돌면서 선택컬럼이 선택된 경우
    for ( i = 0; i < dsOutTrngExptRacer.GetRowCount(); i++ ) {
		if ( dsOutTrngExptRacer.GetRowType(i) != "normal" ) {
            // 필드값에 값이 없는 경우 메세지를 호출 한 후 해당 Row번호를 return 한다.
            if ( dsOutTrngExptRacer.GetColumn(i, "TRNG_RSN_CD") == "001" ) {
                if ( fnc_IsNull(dsOutTrngExptRacer.GetColumn(i, "SANC_ISSUE_NO")) ) {
                    dsOutTrngExptRacer.SetColumn(i, "SANC_ISSUE_NO", trim(dsOutTrngExptRacer.GetColumn(i, "SANC_ISSUE_NO")));
                    fnc_Message(SNIS_BOA_0001, "N", i + 1, "제재번호"); 
                    dsOutTrngExptRacer.row = i;

                    if ( oGrid != null ) {
                        oGrid.SetFocus();
                        oGrid.SetCellPos(oGrid.GetBindCellIndex("body", "SANC_ISSUE_NO"));
                    }
                    return false;
                }
			}
		}
    }

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e02070020-service:saveTrngExptRacer";
    var sInDataSet   = "dsOutTrngExptRacer=dsOutTrngExptRacer:u ";
    var sOutDataSet  = "dsOutTrngExptRacer=dsOutTrngExptRacer ";

	fce_AddParam(Trim("TRNG_RSN_CD      "), cboSTrngRsn.Value     );
	fce_AddParam(Trim("NM_KOR           "), edRacerNm.Value       );
	fce_AddParam(Trim("TRNG_EXPT_STAT_CD"), cboSTrngExptStat.Value);
	fce_AddParam(Trim("STR_DT           "), calStrDt.Value        );
	fce_AddParam(Trim("END_DT           "), calEndDt.Value        );


	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

