function fcChoice(obj)
{
	var sObj = "";
	switch (tab0.TabIndex) {
		case 0:
			sObj = "dsOutTrngExptRacer";
			break;
		case 1:
			sObj = "dsOutRacerIjry";
			break;
		case 2:
			sObj = "dsOutRacerWant";

			//기존 DataSet에 check된 커뮤니티 내용 추가

			var tmpChkedIdx = dsTrngRqst.FindRow("CHK", 1);

			if(tmpChkedIdx != -1){
				for(var i = 0; i < dsTrngRqst.GetRowCount(); i++){
					if(dsTrngRqst.GetColumn(i, "CHK") == 1){					
						dsOutRacerWant.AddRow();
						dsOutRacerWant.SetColumn(dsOutRacerWant.row, "CHK", "1");
						dsOutRacerWant.SetColumn(dsOutRacerWant.row, "RACER_NO", dsTrngRqst.GetColumn(i,"RACER_NO"));
						dsOutRacerWant.SetColumn(dsOutRacerWant.row, "NM_KOR", dsTrngRqst.GetColumn(i,"NM_KOR"));
						dsOutRacerWant.SetColumn(dsOutRacerWant.row, "RACER_GRD_CD", dsTrngRqst.GetColumn(i,"RACER_GRD_CD"));
						dsOutRacerWant.SetColumn(dsOutRacerWant.row, "TRNG_RSN_CD", dsTrngRqst.GetColumn(i,"TRNG_RSN_CD"));
						dsOutRacerWant.SetColumn(dsOutRacerWant.row, "TRNG_RQST_SEQ", dsTrngRqst.GetColumn(i,"TRNG_RQST_SEQ"));

						//희망 훈련 시작/종료일 추가
						dsOutRacerWant.SetColumn(dsOutRacerWant.row, "IJRY_SDT", dsTrngRqst.GetColumn(i,"TRNG_STRT_DT"));
						dsOutRacerWant.SetColumn(dsOutRacerWant.row, "IJRY_EDT", dsTrngRqst.GetColumn(i,"TRNG_END_DT"));

						//휴대폰 번호 추가
						dsOutRacerWant.SetColumn(dsOutRacerWant.row, "CELPON_NO", dsTrngRqst.GetColumn(i,"CELPON_NO"));

						//진행상태 추가
						dsOutRacerWant.SetColumn(dsOutRacerWant.row, "ACCEPT_STAT", dsTrngRqst.GetColumn(i,"ACCEPT_STAT_CHK"));
					}
				}
			}

			break;
	}
	if (sObj == "") return;
	var objObject = Object(sObj);

	var nChkedIdx = objObject.FindRow("CHK", 1);
	if(nChkedIdx==-1) {
        fnc_SetReturnDs1(objObject, objObject.GetCurrow());
	} else {
        var sRowIdx;
        for(var i=0; i<objObject.GetRowCount(); i++) {
            if(objObject.GetColumn(i, "CHK")==1) {
                sRowIdx += i + "^";
            }
        }
        sRowIdx = mid(sRowIdx, 0, length(sRowIdx)-1);
        fnc_SetReturnDs1(objObject, sRowIdx);
	}

	close(true);
}



/*
 * 원본 Dataset의 선택행 값을 return Dataset에 카피한다.
 *
 * param dsSrc - 원본 Dataset 객체
 * param rowIdxs - 선택행 index, 여러개일 경우 '^'문자로 구분한다. (0^1^2^3^4 ...) : Nullable
 * param dsSrc - return Dataset : Nullable
 *
 * return void
 */
function fnc_SetReturnDs1(dsSrc, rowIdx, dsReturn) {

    var nRnum;
    var arrRowIdx;

    if(rowIdx==null) {
        rowIdx = dsSrc.GetCurrow();
    }

    if(dsReturn==null) {
        dsReturn = gdsReturn;
    }
    arrRowIdx = split(rowIdx, "^");

    dsReturn.Copy(dsSrc);
    dsReturn.DeleteAll();

    for(var i=0; i<length(arrRowIdx); i++) {
        nRnum = dsReturn.AppendRow();
        dsReturn.CopyRow(nRnum, dsSrc, arrRowIdx[i]);
    }
}

function fcChngText(sTrngRsnCd) 
{
	if (sTrngRsnCd == "005") {
		stSancRsn.text = "주선보류사유";
		stSancIssueNo.text = "주선보류번호";
	} else {
		stSancRsn.text = "제재사유";
		stSancIssueNo.text = "제재번호";
	}
}
//훈련신청내역 (커뮤니티) 저장
function Tab0_tab3_btnSave_OnClick(obj){
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsTrngRqst) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	for(var i = 0; i < dsTrngRqst.GetRowCount(); i++){
		if(dsTrngRqst.GetColumn(i,"ACCEPT_STAT_CHK") != dsTrngRqst.GetColumn(i,"ACCEPT_STAT")){
			if(dsTrngRqst.GetColumn(i,"ACCEPT_STAT") == "300" && fnc_IsNull(dsTrngRqst.GetColumn(i,"REJECT_REASON"))){
				fnc_Message(L_COM_CNF_0000, "N", "반려사유를 입력하세요. [" + (i + 1) + " 행 ]");
				dsTrngRqst.rowpos = i;
				return false;
			}
			else if(dsTrngRqst.GetColumn(i,"ACCEPT_STAT") == "200"){
				dsTrngRqst.SetColumn(i, "REJECT_REASON", "");
			}
		}
	}

	var sServiceName = "e02070021-service:saveTrngRqst";
    var sInDataSet   = "dsTrngRqst=dsTrngRqst:u ";
    var sOutDataSet  = "";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
