/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutTrngAsk.ClearData();
    dsOutTrngAskRacer.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e02070030-service:searchTrngAsk";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTrngAsk=dsOutTrngAsk ";

	fce_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fce_AddParam(Trim("END_DT          "), calEndDt.Value       );
	fce_AddParam(Trim("NM_KOR          "), edRacerNm.Text       );
	fce_AddParam(Trim("TRNG_ASK_STAT_CD"), cboSTrngAskStat.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchRacerCheck(obj) {
	return true;
}

function fcSearchRacer(obj) {
    if ( !fcSearchRacerCheck(obj) ) return;

	var sServiceName = "e02070030-service:searchTrngAskRacer";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTrngAskRacer=dsOutTrngAskRacer ";

	fce_AddParam(Trim("TRNG_ASK_SEQ"), dsOutTrngAsk.GetColumn(dsOutTrngAsk.currow, "TRNG_ASK_SEQ"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutTrngAsk) && !fnc_DatasetChangeCheck(dsOutTrngAskRacer)  )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e02070030-service:saveTrngAskList";
    var sInDataSet   = "dsOutTrngAsk=dsOutTrngAsk:u "
                     + "dsOutTrngAskRacer=dsOutTrngAskRacer:u ";
    var sOutDataSet  = "dsOutTrngAsk=dsOutTrngAsk ";

	fce_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fce_AddParam(Trim("END_DT          "), calEndDt.Value       );
	fce_AddParam(Trim("NM_KOR          "), edRacerNm.Text       );
	fce_AddParam(Trim("TRNG_ASK_STAT_CD"), cboSTrngAskStat.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

