/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 
	switch ( obj.GetForm().id ) {
		case "divBtnTrngAsk" :

            var sArg = "Arg_sTrngAskSeq=" + quote("");
            if ( Dialog("form::E02070035.xml", sArg, 834, 450, "") ) fcSearch(divSearch.btnSearch);
            fcSearch(divSearch.btnSearch);
/*		
            if ( fnc_DatasetChangeCheck(dsOutTrngAskRacer) ) {
                if ( fnc_Message(SNIS_COM_1002, 'Y') ) return;
            }
			dsOutTrngAsk.AddRow();         //Record Add
			grdTrngAsk.SetFocus();         //입력 첫 항목으로 Focus
*/
			break;
        case "divBtnTrngAskRacer" : 
			dsOutTrngAskRacer.AddRow();         //Record Add

			var nRow = dsOutTrngAskRacer.currow;
			dsOutTrngAskRacer.SetColumn(nRow, "TRNG_ASK_SEQ", dsOutTrngAsk.GetColumn(dsOutTrngAsk.currow, "TRNG_ASK_SEQ"));

			grdTrngAskRacer.SetFocus();         //입력 첫 항목으로 Focus
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	switch ( obj.GetForm().id ) {
		case "divBtnTrngAsk" :
			if (dsOutTrngAsk.GetColumn(dsOutTrngAsk.row,"TRNG_ASK_STAT_CD")>="001") {
				if (dsOutTrngAsk.GetColumn(dsOutTrngAsk.row,"TRNG_ASK_STAT_CD")=="001") {
					fnc_message("훈련요청을 취소후 삭제가 가능합니다.","N");
				} else {
					fnc_message("현재 훈련중인 자료는 삭제할 수 없습니다.","N");
				}
				return;
			}

			if ( !fnc_DeleteCheck(dsOutTrngAsk, "chk", "1") ) return;
			grdTrngAsk.SetFocus();                                  //Record 삭제 후 Grid로 Focus
			dsOutTrngAsk_OnRowPosChanged();
			break;

		case "divBtnTrngAskRacer" :
			if (dsOutTrngAsk.GetColumn(dsOutTrngAsk.row,"TRNG_ASK_STAT_CD")>="001") {
				if (dsOutTrngAsk.GetColumn(dsOutTrngAsk.row,"TRNG_ASK_STAT_CD")=="001") {
					fnc_message("훈련요청을 취소후 선수 삭제가 가능합니다.","N");
				} else {
					fnc_message("현재 훈련중인 선수를 제외할 수는 없습니다.","N");
				}
				return;
			}
			if ( !fnc_DeleteCheck(dsOutTrngAskRacer, "chk", "1") ) return;
			grdTrngAskRacer.SetFocus();                                  //Record 삭제 후 Grid로 Focus
			break;
	}
}

