*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
		fce_AddCodeRow(dsOutSTrngAskStat, "전체", "");
		cboSTrngAskStat.Index = 0;
//		fcSearch();
		return;
	}

	// 조회 후
	if ( sServiceName == "e02070030-service:searchTrngAsk" ) {
        fnc_SetStatus(SNIS_BOA_0003, dsOutTrngAsk.GetRowCount());
		return;
	}
	// 조회 후
	if ( sServiceName == "e02070030-service:searchTrngAskRacer" ) {
		return;
	}

	// 저장 후
	if ( sServiceName == "e02070030-service:saveTrngAskList" ) {
		return;
	}

	//  상태변경후
	if ( sServiceName == "e02070030-service:UpdateTrngAskStcd" ) {
		// 메세지 전송
		fcSendMessage();
	}



	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

