/*-------------------------------------------------------------------------------------------------+
>>  Grid Double Click시 수시훈련상세조회
+-------------------------------------------------------------------------------------------------*/
function grdTrngAsk_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    var sArg = "Arg_sTrngAskSeq=" + quote(dsOutTrngAsk.GetColumn(nRow, "TRNG_ASK_SEQ"));
