}

function fcBtnOnClick(obj)
{
	var sendcnt = 0;
	if (dsOutTrngAsk.findrow("CHK","1") == -1) return;

	sSendMesg = "";
	if (obj.id == "btnTrngAsk" || obj.id == "btnTrngAskCncl" ) {
		if (obj.id == "btnTrngAsk") {
			// 상태가 훈련요청상태가 아닌 자료가 있는 경우 종료
			for (var iRow=0;iRow<dsOutTrngAsk.rowcount();iRow++) {
				if (dsOutTrngAsk.GetColumn(iRow,"CHK") == "1") {
					if (dsOutTrngAsk.GetColumn(iRow,"TRNG_ASK_STAT_CD") <> "000") {
						fnc_message("훈련등록 상태만 훈련요청이 가능합니다.","N");
						dsOutTrngAsk.row = iRow;
						return;
					} else {
						dsOutTrngAsk.SetColumn(iRow,"TRNG_ASK_STAT_CD", "001");
						sSendMesg = "선수훈련 요청처리건이 있습니다.";
					}
				}
			}
		} else {
			// 상태가 훈련등록상태가 아닌 자료가 있는 경우 종료
			for (var iRow=0;iRow<dsOutTrngAsk.rowcount();iRow++) {
				if (dsOutTrngAsk.GetColumn(iRow,"CHK") == "1") {
					if (dsOutTrngAsk.GetColumn(iRow,"TRNG_ASK_STAT_CD") <> "001") {
						fnc_message("훈련요청상태만 훈련취소가 가능합니다.","N");
						dsOutTrngAsk.row = iRow;
						return;
					} else {
						dsOutTrngAsk.SetColumn(iRow,"TRNG_ASK_STAT_CD", "000");
						sSendMesg = "선수훈련 요청취소건이 있습니다.";
					}
				}
			}
		}

		var sServiceName = "e02070030-service:UpdateTrngAskStcd";
		var sInDataSet   = "dsOutTrngAsk=dsOutTrngAsk:u ";
		var sOutDataSet  = "dsOutTrngAsk=dsOutTrngAsk ";

		fce_AddParam(Trim("STR_DT          "), calStrDt.Value       );
		fce_AddParam(Trim("END_DT          "), calEndDt.Value       );
		fce_AddParam(Trim("NM_KOR          "), edRacerNm.Text       );
		fce_AddParam(Trim("TRNG_ASK_STAT_CD"), cboSTrngAskStat.Value);

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	}
}



function fcSendMessage() 
{
	var sSendUserId   = gdsUser.GetColumn(0, "USER_ID");		
	var sMsgReceiveId = "";
	var sTitle        = "선수훈련 요청";
	var sLinkMnId     = "D07000001";

	for (var iCnt=0;iCnt<dsMesgRecvr.RowCount();iCnt++) {
		if (iCnt <> 0) sMsgReceiveId += ", ";
		sMsgReceiveId += dsMesgRecvr.GetColumn(iCnt, "CD");	// 수신자 사번
	}
	fnc_SendMessage(sSendUserId								// 송신자 사번 
				, sMsgReceiveId 						// 수신자 사번 
				, sTitle	    					    // 제목
				, sLinkMnId 							// 수신자 번호
				, sSendMesg								// 내용 
				, "comSendMsg"
				, "CANBOA"
				);	
	sSendMesg = "";
}
