/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	sBUTTONLIST   = 'T' + sInpt + 'FTFT';
//	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	//공통코드 조회
	fce_GetComboDs(dsInComCd);

	// 화면 초기화
	fcInit();

	if ( fnc_IsNull(Arg_sTrngAskSeq) ) {
        dsOutTrngAsk.AddRow();
        //calStrDt.Value = fce_GetSysdate("YYYYMMDD");
        //calEndDt.Value = AddDate(calStrDt.Value, 1);
        dsOutTrngAsk.SetColumn(dsOutTrngAsk.currow, "TRNG_ASK_STAT_CD", "001");
	} else {
        fcSearch();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
