/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 
	switch ( obj.GetForm().id ) {
		case "divBtnRowTrngAskRacer" :
            var sArg = "Arg_sTrngAskSeq=" + quote("");
            var sReturn = Dialog("form::E02070032.xml", sArg, 834, 450, "");

            if ( sReturn == false ) return;

            var nRow = dsOutTrngAskRacer.GetRowCount();
            for ( var i = 0; i < sReturn.length; i++ ) {
                dsOutTrngAskRacer.AddRow();

                var sSplit = split(sReturn[i], "|");
                var j = 0;
                dsOutTrngAskRacer.SetColumn(nRow, "TRNG_EXPT_SEQ", sSplit[j++]);
                dsOutTrngAskRacer.SetColumn(nRow, "RACER_NO"     , sSplit[j++]);
                dsOutTrngAskRacer.SetColumn(nRow, "NM_KOR"       , sSplit[j++]);
                dsOutTrngAskRacer.SetColumn(nRow, "RACER_GRD_CD" , sSplit[j++]);
                dsOutTrngAskRacer.SetColumn(nRow, "TRNG_RSN_CD"  , sSplit[j++]);
                dsOutTrngAskRacer.SetColumn(nRow, "REST_DD"      , sSplit[j++]);
                nRow++;
            }

			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	switch ( obj.GetForm().id ) {
		case "divBtnRowTrngAskRacer" :
			if ( !fnc_DeleteCheck(dsOutTrngAskRacer, "chk", "1") ) return;
			break;
	}
}

