/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {

		return;
	}

	// 조회 후
	if ( sServiceName == "e02070030-service:searchTrngAsk" ) {
        dsOutTrngAskRacer.AddColumn("chk");
		return;
	}

	// 저장 후
	if ( sServiceName == "e02070030-service:saveTrngAsk" ) {
        close(true);
		return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

