function dsOutTrngAsk_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
    if ( !(strColumnID == "TRNG_ASK_STR_DT" || strColumnID == "TRNG_ASK_END_DT") ) return;

	var sStrDt = obj.GetColumn(nRow, "TRNG_ASK_STR_DT");
	var sEndDt = obj.GetColumn(nRow, "TRNG_ASK_END_DT");

    if ( fnc_IsNull(sStrDt) || fnc_IsNull(sEndDt) ) {
        obj.SetColumn(nRow, "TRNG_ASK_DD_NUM", "");
    } else {
        if ( sEndDt >= sStrDt ) {
            var nDate  = DateTime(sEndDt) - DateTime(sStrDt);
            obj.SetColumn(nRow, "TRNG_ASK_DD_NUM", nDate + 1);
        } else {
            fnc_Message(SNIS_BOA_B011, "N", "시작일", "종료일");
            obj.SetColumn(nRow, strColumnID, varOldValue);
        }
    }
}
