/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutTrngAskRacer.ClearData();
    dsOutTrngExptRacer.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e02070030-service:searchTremTrngAskRacer";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTrngAskTerm=dsOutTrngAskTerm "
                     + "dsOutTrngAskRacer=dsOutTrngAskRacer "
                     + "dsOutTrngExptRacer=dsOutTrngExptRacer ";

	fce_AddParam(Trim("TRNG_PLC_CD     "), cboTrngPlc.Value     );
	fce_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fce_AddParam(Trim("END_DT          "), calEndDt.Value       );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

