}

/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
    if ( dsOutTrngAskTerm.GetRowCount() > 4 ) {
        fnc_Message(SNIS_BOA_B011, "N", "인쇄기간", "4일");
        return;
    }

    dsOutTrngAskRacerPrint.ClearData();
    for(var i = 0; i < dsOutTrngAskRacer.rowcount; i++)
    {
		dsOutTrngAskRacerPrint.AddRow();
		dsOutTrngAskRacerPrint.SetColumn(i,"column0",i+1);
		dsOutTrngAskRacerPrint.SetColumn(i,"column1",dsOutTrngAskRacer.GetColumn(i,0));
		dsOutTrngAskRacerPrint.SetColumn(i,"column2",dsOutTrngAskRacer.GetColumn(i,1));
		dsOutTrngAskRacerPrint.SetColumn(i,"column3",dsOutTrngAskRacer.GetColumn(i,2));
		dsOutTrngAskRacerPrint.SetColumn(i,"column4",dsOutTrngAskRacer.GetColumn(i,3));
    }

	dsOutTrngAskTermPrint.Clear();
	dsOutTrngAskTermPrint.AddColumn("temp");
	dsOutTrngAskTermPrint.AddRow();
    for(var i = 0; i < dsOutTrngAskTerm.rowcount; i++)
    {
		dsOutTrngAskTermPrint.AddColumn("column"+i);
		dsOutTrngAskTermPrint.SetColumn(0,"column"+i,replace(substr(dsOutTrngAskTerm.GetColumn(i,"TRNG_DATE"),5),"-","."));
    }

    var startDay = dsOutTrngAskTerm.GetColumn(0,"TRNG_DATE");
    var endDay = dsOutTrngAskTerm.GetColumn(dsOutTrngAskTerm.rowcount-1,"TRNG_DATE");

    var param1 = replace(substr(startDay,2,8),"-",".");
    var param2 = replace(substr(endDay,5,5),"-",".");
    var param3 = substr(startDay,11,1);
    var param4 = substr(endDay,11,1);

    var logImgUrl = GBL_REPORT_URL + "log.jpg";
    var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
    var sRptNm    = "e02079036_ask_racer.jrf";               //레포트 파일명
    var sDataSet  = "ask_term=dsOutTrngAskTermPrint ask_racer=dsOutTrngAskRacerPrint expt_racer=dsOutTrngExptRacer";   //"UBI 쿼리명, MIP Dataset명"
    var sArg      = " IMG_URL="+logImgUrl; 
        sArg      += " param1=" + param1;
        sArg      += " param2=" + param2;
        sArg      += " param3=" + param3;
        sArg      += " param4=" + param4;
    var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

    fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);

    return;
}

