/*-------------------------------------------------------------------------------------------------+
>>  Grid 생성
+-------------------------------------------------------------------------------------------------*/
function fcCreateGrid() {
    Destroy("grdTrngAskRacer");

    var sContents = "";
    sContents = '                  <contents>';
    sContents = sContents + fcGetColumn() + fcGetHead() + fcGetBody();
    sContents = sContents + '      </contents>';


    sGridType   = 'AutoFit="TRUE" '
                + 'AutoFitEndLine="Hide" '
                + 'BindDataset="dsOutTrngAskRacer" '
                + 'BKColor="user15" '
                + 'BkColor2="user16" '
                + 'BkSelColor="user17" '
                + 'BoldHead="true" '
                + 'Border="Flat" '
                + 'BorderColor="User3" '
                + 'Bottom="579" '
                + 'ColSizing="True" '
                + 'Enable="true" '
                + 'EndLineColor="default" '
                + 'FillArea="TRUE" '
                + 'HeadBorder="Flat" '
                + 'HeadHeight="20" '
                + 'Height="310" '
                + 'InputPanel="FALSE" '
                + 'Left="6" '
                + 'LineColor="user18" '
                + 'MinWidth="100" '
                + 'OnHeadClick="fcGridSort" '
                + 'Right="1264" '
                + 'SelColor="user19" '
                + 'Style="snis_grid" '
                + 'TabOrder="8" '
                + 'TabStop="true" '
                + 'Top="88" '
                + 'UseDBuff="true" '
                + 'UsePopupMenu="true" '
                + 'UseSelColor="true" '
                + 'Visible="true" '
                + 'VLineColor="default" '
                + 'WheelScrollRow="10" '
                + 'Width="998" ';

    create("Grid","grdTrngAskRacer", sGridType);
	grdTrngAskRacer.contents = sContents;
}

/*-------------------------------------------------------------------------------------------------+
>>  Grid Column 생성
+-------------------------------------------------------------------------------------------------*/
function fcGetColumn() {
    var sColumn = "";
    sColumn = '					<columns>'
            + '                     	<col fix="left" width="22"/>';

    for ( var i = 0; i < dsOutTrngAskTerm.GetRowCount(); i++ ) {
        sColumn += '                    <col width="80"/>  ';
    }


    sColumn += '					</columns>';
    return sColumn;
}

/*-------------------------------------------------------------------------------------------------+
>>  Grid Head 생성
+-------------------------------------------------------------------------------------------------*/
function fcGetHead() {
    var sHead = "";
        sHead = '     <head>  '
              + '     <cell bkcolor="user20" col="0"  color="user21" display="text" font="굴림,9" text=""/>';

    for ( var i = 0; i < dsOutTrngAskTerm.GetRowCount(); i++ ) {
        var sDate = dsOutTrngAskTerm.GetColumn(i, "TRNG_DATE");
        sHead += '    <cell bkcolor="user20" col="'+ (1 + i) +'" color="user21" display="text" font="굴림,9" text="' + sDate + '"/>  ';
    }

    sHead   += '</head>';
    return sHead;
}

/*-------------------------------------------------------------------------------------------------+
>>  Grid Body 생성
+-------------------------------------------------------------------------------------------------*/
function fcGetBody() {
    var sBody = "";
        sBody = '     <body>  ' 
              + '     <cell col="0" colid="chk" expr="row&#32;+&#32;1" align="center" />';

    for ( var i = 0; i < dsOutTrngAskTerm.GetRowCount(); i++ ) {
        var sDate = "TRNG_DD_" + dsOutTrngAskTerm.GetColumn(i, "TRNG_DD");
        sBody += '    <cell align="center" col="'+ (1 + i) +'" colid="'+ sDate +'" display="text" />';
    }

    sBody += '</body>';

    return sBody;
}
