function fcToExcel(obj) {
	if ( tbScore.TabIndex == 0 ) {
		fnc_OpenExcelExport("tbScore.tb0.grdRankScr");         //입력 첫 항목으로 Focus
	}

	if ( tbScore.TabIndex == 1 ) {
		fnc_OpenExcelExport("tbScore.tb1.grdAcdntScr");        //입력 첫 항목으로 Focus
	}
}

