/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboMbr.Index = 0;

        fce_GetRaceTms(cboSStndYear.Value,cboMbr.Value,"","dsOutSRaceTms");
        fce_GetRaceTms(cboSStndYear.Value,cboMbr.Value,"","dsOutERaceTms");
		return;
	}

	// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        cboSRaceTms.Index = 0;
        cboERaceTms.Index = 0;

        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
            if ( nIndexRow > -1 ) cboSRaceTms.Index = nIndexRow;
        }

        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
            if ( nIndexRow > -1 ) cboERaceTms.Index = nIndexRow;
        }

        //fcSearchRaceDayOrd();
		return;
	}	


	// 코스별 2착률
	if( sServiceName = "e02080010-service:searchCourse1" ) {
        if ( dsOutCourse1.rowcount == 0 ) {
            return;
        }

        var logImgUrl = GBL_REPORT_URL + "log.jpg";
        var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
        var sRptNm    = "e02080010_1.jrf";               //레포트 파일명
        var sDataSet  = "sql_course2=dsOutCourseRpt";   //"UBI 쿼리명, MIP Dataset명"
        var sArg      = " IMG_URL="+logImgUrl; 
            sArg     += " STND_YEAR=" + cboSStndYear.Value; 
            sArg     += " STMS=" + cboSRaceTms .Value;
            sArg     += " ETMS=" + cboERaceTms .Value;

        for ( var i = 0; i < dsOutCourse2.rowcount; i++ ) {
            dsOutCourseRpt.setColumn(i, "R1"       , dsOutCourse1.getColumn(i / 5, "R1"));
            dsOutCourseRpt.setColumn(i, "CS"       , dsOutCourse1.getColumn(i / 5, "CS"));
            dsOutCourseRpt.setColumn(i, "RANK2C"   , dsOutCourse2.getColumn(i, "RANK2C"));
            dsOutCourseRpt.setColumn(i, "CNT"      , dsOutCourse2.getColumn(i, "CNT"));
        }

        fnc_DatasetPrint(dsOutCourseRpt);
        return;
        var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

        fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}	

	// 평균주회타임
    if( sServiceName = "e02080010-service:searchAvgRaceTime" ) {
        if ( dsOutAvgRaceTime.rowcount == 0 ) {
            return;
        }

        var logImgUrl = GBL_REPORT_URL + "log.jpg";
        var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
        var sRptNm    = "e02080010_2.jrf";               //레포트 파일명
        var sDataSet  = " sql_avgratetime=dsOutAvgRaceTime ";   //"UBI 쿼리명, MIP Dataset명"
            sDataSet += " sql_avgratetimetotal=dsOutAvgRaceTimeTotal ";
        var sArg      = " IMG_URL="+logImgUrl; 
            sArg     += " STNDYEAR="+cboSStndYear.Value;

        var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

        fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
    }



	// 정번별 코스현황
    if( sServiceName = "e02080010-service:searchEntryCourseRegCount" ) {
        if ( dsOutEntryCourseReg.rowcount == 0 ||
             dsOutEntryCourseRankCount.rowcount == 0) {
            return;
        }

        var logImgUrl = GBL_REPORT_URL + "log.jpg";
        var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
        var sRptNm    = "e02080010_4.jrf";               //레포트 파일명
        var sDataSet  = "sql_entryreg=dsOutEntryCourseReg";   //"UBI 쿼리명, MIP Dataset명"
        var sArg      = " IMG_URL="+logImgUrl; 
            sArg     += " INCMPCNT="+dsOutEntryCourseRankCount.getColumn(0, "STR");
            sArg     += " CMPLTCNT="+dsOutEntryCourseRankCount.getColumn(0, "STR2");

        var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

        fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
    }

	// 진입코스별 성적현황, 코스별 입상현황
    if( sServiceName = "e02080010-service:searchEntryCourseRankCount" ) {
        if ( dsOutEntryCourseRank.rowcount == 0 ||
             dsOutEntryCourseRankCount.rowcount == 0) {
            //return;
        }

        var logImgUrl = GBL_REPORT_URL + "log.jpg";
        var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
        var sRptNm    = "e02080010_7.jrf";               //레포트 파일명
        var sDataSet  = "sql_entryrank=dsOutEntryCourseRank";   //"UBI 쿼리명, MIP Dataset명"
        var sArg      = " IMG_URL="+logImgUrl; 
            sArg     += " INCMPCNT="+dsOutEntryCourseRankCount.getColumn(0, "STR");
            sArg     += " CMPLTCNT="+dsOutEntryCourseRankCount.getColumn(0, "STR2");

        var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

        fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
    }


	// 코스별 선수 경주기록
    if( sServiceName = "e02080010-service:searchEntryCourseRacer" ) {
        if ( dsOutEntryCourseRacer.rowcount == 0 ) {
            return;
        }

	    var yStr    = cboSStndYear.Value+"년";
	    var sTmsStr = cboSRaceTms .Value+"회";
	    var eTmsStr = cboERaceTms .Value+"회";

        var logImgUrl = GBL_REPORT_URL + "log.jpg";
        var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
        var sRptNm    = "e02080010_5.jrf";               //레포트 파일명
        var sDataSet  = "sql_entryracer=dsOutEntryCourseRacer";   //"UBI 쿼리명, MIP Dataset명"
        var sArg      = " IMG_URL="+logImgUrl; 
            sArg     += " YSTR="+yStr;
            sArg     += " SSTR="+sTmsStr;
            sArg     += " ESTR="+eTmsStr;

        var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

        fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
    }


    // S.T 및 소개항주 1위자 입상율 현황
    if( sServiceName = "e02080010-service:searchIntroTm1" ) {
        if ( dsOutIntroRank.rowcount == 0 ) {
            return;
        }

        var logImgUrl = GBL_REPORT_URL + "log.jpg";
        var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
        var sRptNm    = "e02080010_6.jrf";               //레포트 파일명
        var sDataSet  = "sql_introrank=dsOutIntroRank";   //"UBI 쿼리명, MIP Dataset명"
        var sArg      = " IMG_URL="+logImgUrl; 

        var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

        fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
    }


	// 1착 전법 현황
    if( sServiceName = "e02080010-service:searchEntryCourseRank1" ) {
        if ( dsOutEntryCourseRank1.rowcount == 0 ) {
            return;
        }

	    var yStr    = cboSStndYear.Value+"년";
	    var sTmsStr = cboSRaceTms .Value+"회";
	    var eTmsStr = cboERaceTms .Value+"회";

        var logImgUrl = GBL_REPORT_URL + "log.jpg";
        var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
        var sRptNm    = "e02080010_8.jrf";               //레포트 파일명
        var sDataSet  = "sql_entryrank1=dsOutEntryCourseRank1";   //"UBI 쿼리명, MIP Dataset명"
        var sArg      = " IMG_URL="+logImgUrl;            
            sArg     += " YSTR="+yStr;
            sArg     += " SSTR="+sTmsStr;
            sArg     += " ESTR="+eTmsStr;
        var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

        fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
    }


	// 진입코스별 ST타임
	if( sServiceName = "e02080010-service:searchEntryCourse" ) {
        if ( dsOutEntryCourse.rowcount == 0 ) {
            return;
        }

        var logImgUrl = GBL_REPORT_URL + "log.jpg";
        var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
        var sRptNm    = "e02080010_9.jrf";               //레포트 파일명
        var sDataSet  = "sql_entrycourse=dsOutEntryCourse";   //"UBI 쿼리명, MIP Dataset명"
        var sArg      = " IMG_URL="+logImgUrl; 
        var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

        fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

