function cboSStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	        fce_GetRaceTms(   cboSStndYear  .value
                        , cboMbr.Value
                        , 
                        , "dsOutSRaceTms");	

	        fce_GetRaceTms(   cboSStndYear  .value
                        , cboMbr.Value
                        , 
                        , "dsOutERaceTms");	
}



function btnPrint_OnClick(obj)
{
	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";

    switch(obj.id) {
        case "Button0" :    break;
        case "Button2" :    // 평균주회타임
                            sServiceName = "e02080010-service:searchAvgRaceTime";
                            sInDataSet   = "";
                            sOutDataSet  = "dsOutAvgRaceTime=dsOutAvgRaceTime ";        
                            sOutDataSet += "dsOutAvgRaceTimeTotal=dsOutAvgRaceTimeTotal ";
                            break;            
        case "Button3" :    // 정번별 코스현황
                            sServiceName = "e02080010-service:searchEntryCourseRegCount";
                            sInDataSet   = "";
                            sOutDataSet  = "dsOutEntryCourseReg=dsOutEntryCourseReg ";        
                            sOutDataSet += "dsOutEntryCourseRankCount=dsOutEntryCourseRankCount ";
                            break;        
        case "Button4" :    // 코스별 선수 경주기록
                            sServiceName = "e02080010-service:searchEntryCourseRacer";
                            sInDataSet   = "";
                            sOutDataSet  = "dsOutEntryCourseRacer=dsOutEntryCourseRacer ";        
                            break;         
        case "Button5" :    // S.T 및 소개항주 1위자 입상율 현황
                            sServiceName = "e02080010-service:searchIntroTm1";
                            sInDataSet   = "";
                            sOutDataSet  = "dsOutIntroRank=dsOutIntroRank ";
                            break;
        case "Button1" :
        case "Button6" :    // 진입코스별 성적현황
                            sServiceName = "e02080010-service:searchEntryCourseRankCount";
                            sInDataSet   = "";
                            sOutDataSet  = "dsOutEntryCourseRank=dsOutEntryCourseRank ";        
                            sOutDataSet += "dsOutEntryCourseRankCount=dsOutEntryCourseRankCount ";
                            break;        
        case "Button7" :    // 1착 전법 현황
                            sServiceName = "e02080010-service:searchEntryCourseRank1";
                            sInDataSet   = "";
                            sOutDataSet  = "dsOutEntryCourseRank1=dsOutEntryCourseRank1";
                            break;        
        case "Button8" :    // 진입코스별 ST타임
                            sServiceName = "e02080010-service:searchEntryCourse";
                            sInDataSet   = "";
                            sOutDataSet  = "dsOutEntryCourse=dsOutEntryCourse";
                            break;
        case "Button9" :    // 코스별 2착률
                            sServiceName = "e02080010-service:searchCourse1";
                            sInDataSet   = "";
                            sOutDataSet  = "dsOutCourse1=dsOutCourse1 ";
                            sOutDataSet += "dsOutCourse2=dsOutCourse2 ";
                            break;                            
    } // end switch

	fce_AddParam("STND_YEAR", cboSStndYear.Value);
	fce_AddParam("MBR_CD"    , cboMbr.Value);	
	fce_AddParam("STMS"      , cboSRaceTms .Value);
	fce_AddParam("ETMS"      , cboERaceTms .Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
