/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
    //if ( !fnc_IsMandatory(divSearchCond.calStrDt, "시작일") ) return false;
    //if ( !fnc_IsMandatory(divSearchCond.calEndDt, "종료일") ) return false;

    fcInit();

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck() ) return;

	var sServiceName = "e02080130-service:searchRacerRecord";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerRecord=dsOutRacerRecord";

	fce_AddParam(Trim("STND_YEAR"), cboStndYear.Value);
	fce_AddParam(Trim("MBR_CD"), cboMbr.Value);
	fce_AddParam(Trim("RACER_NO1"), edRacerNo1.Text);
	fce_AddParam(Trim("RACER_NO2"), edRacerNo2.Text);
	fce_AddParam(Trim("ST_MTHD_CD"),cboStMthdCd.value 	); //온라인스타트여부

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


