function dsOutRacerRecord_OnRowPosChanged(obj,nOldRow,nRow)
{
    dsOutOrgan.ClearData();
    var sServiceName = "e04020010-service:searchOrgan";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutOrgan=dsOutOrgan";

    fce_AddParam(Trim("STND_YEAR"), dsOutRacerRecord.GetColumn(dsOutRacerRecord.currow, "STND_YEAR"));
	fce_AddParam(Trim("MBR_CD   "), cboMbr.Value);
	fce_AddParam(Trim("TMS      "), dsOutRacerRecord.GetColumn(dsOutRacerRecord.currow, "TMS"));
	fce_AddParam(Trim("DAY_ORD  "), dsOutRacerRecord.GetColumn(dsOutRacerRecord.currow, "DAY_ORD"));
    fce_AddParam(Trim("RACE_NO  "), dsOutRacerRecord.GetColumn(dsOutRacerRecord.currow, "RACE_NO"));
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);


    //경주결과조회 (위반행위)
    dsOutVoilAct.ClearData();
	var sServiceName = "e04020010-service:searchVoilAct";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutVoilAct=dsOutVoilAct";

    fce_AddParam(Trim("STND_YEAR"), dsOutRacerRecord.GetColumn(dsOutRacerRecord.currow, "STND_YEAR"));
	fce_AddParam(Trim("MBR_CD   "), cboMbr.Value);
	fce_AddParam(Trim("TMS      "), dsOutRacerRecord.GetColumn(dsOutRacerRecord.currow, "TMS"));
	fce_AddParam(Trim("DAY_ORD  "), dsOutRacerRecord.GetColumn(dsOutRacerRecord.currow, "DAY_ORD"));
    fce_AddParam(Trim("RACE_NO  "), dsOutRacerRecord.GetColumn(dsOutRacerRecord.currow, "RACE_NO"));
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

    //경주결과조회 (사고행위)
    dsOutAcdntStts.ClearData();
	var sServiceName = "e04020010-service:searchAcdntStts";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutAcdntStts=dsOutAcdntStts";

    fce_AddParam(Trim("STND_YEAR"), dsOutRacerRecord.GetColumn(dsOutRacerRecord.currow, "STND_YEAR"));
	fce_AddParam(Trim("MBR_CD   "), cboMbr.Value);
	fce_AddParam(Trim("TMS      "), dsOutRacerRecord.GetColumn(dsOutRacerRecord.currow, "TMS"));
	fce_AddParam(Trim("DAY_ORD  "), dsOutRacerRecord.GetColumn(dsOutRacerRecord.currow, "DAY_ORD"));
    fce_AddParam(Trim("RACE_NO  "), dsOutRacerRecord.GetColumn(dsOutRacerRecord.currow, "RACE_NO"));
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function edNmKor_OnClick(obj)
{
	var sArg = "Arg_sRacerNm=" ;
	var sResponse = Dialog("form::E03010012.xml", sArg, 834, 450, "");

	if ( !fnc_IsNull(sResponse) ) {
		if ( obj.id == "edRacerNm1" ) {
			edRacerNo1.Text = sResponse[0];
			edRacerNm1.Text =  sResponse[1];
		} else {
			edRacerNo2.Text = sResponse[0];
			edRacerNm2.Text =  sResponse[1];
		}
	}
}

function edRacerNo_OnClick(obj)
{
	var sArg = "Arg_sRacerNm=" ;
	var sResponse = Dialog("form::E07040021.xml", sArg, 834, 450, "");

	if ( !fnc_IsNull(sResponse) ) {
		if ( obj.id == "edRacerNo1" ) {
			edRacerNo1.Text = sResponse[0];
			edRacerNm1.Text =  sResponse[1];
		} else {
			edRacerNo2.Text = sResponse[0];
			edRacerNm2.Text =  sResponse[1];
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  최고정번수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function setLocation(){
	var oriBottom = grdOrgan.Bottom;
	var extHeight = 0;
	var rowHeight = 19;
	var rowCnt = dsOutOrgan.GetRowCount();
	var gap = 10;

	if(rowCnt < 6) rowCnt = 6;

	grdOrgan.Height = (rowCnt + 1) * rowHeight;

	grdVoilAct.Top = grdOrgan.Bottom + gap;
	grdAcdntStts.Top = grdOrgan.Bottom + gap;
}
