/***************************************************************************************************
*                                          화면 변수 선언부                                        *
***************************************************************************************************/
var sFormId       = 'E02080311';
var sBUTTONLIST   = 'TFFTFT';                          //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트
var F_sSetCellProp = "";
var F_asColumnList = Array();
var gF_TmsGubun = "S";
var sFirst		= true;
/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

    //년도조회
    fce_SetStndYear(dsOutSStndYear, toInteger(fce_GetSysdate("YYYY")));
    cboSStndYear.Index = 0;
    cboEStndYear.Index = 0;

	//공통코드 조회
	fce_GetComboDs(dsInComCd);
}


/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}


// Dataset 초기화
function fcInit(obj) {
    edtRacerNm.Text = "";
    edtRacerNo.Text = "";
    chkAllRecord.Value = 0;
    dsRacerVoilAct.ClearData();
}


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    fnc_OpenExcelExport("grdRacerVoilAct");
}


// 선수명 조회버튼 이벤트
function btnSearchRacer_OnClick(obj,nX,nY) {
	var sResponse = Dialog("form::E03010012.xml", "", 834, 450, "");
    var sPtoName  = sResponse[3];

    if ( !fnc_IsNull(sResponse) ) {
		edtRacerNo.Text = sResponse[0];
		edtRacerNm.Text = sResponse[1];

		if (fnc_IsNull(sPtoName)) return;
    }
}



// 조회버튼 이벤트
function fcSearch(obj) {
    var sServiceName = ""; 
    var sInDataSet   = "";
    var sOutDataSet  = "";
    var sStr_Dt		 = calStrDt.Value;
    var sEnd_Dt		 = calEndDt.Value;	

    if(chkAllRecord.Value == 1) {
		sStr_Dt = "";
		sEnd_Dt = "";
    }

    // 타 화면에서 현재화면 조회하는 경우 fcSearchCheck() 하지 않음
    if(!IsExistVar("Arg_preMenuID")) {
		if(!fcSearchCheck()) return;
	}	

	sServiceName = "e02080310-service:searchAllRacerVoilAct";
	sInDataSet   = "";
	sOutDataSet  = "dsRacerVoilAct=dsRacerVoilAct";

    fce_AddParam("MBR_CD"   , cboSMbr.Value);
    fce_AddParam("STR_DT"   , sStr_Dt);
	fce_AddParam("END_DT"   , sEnd_Dt);
    fce_AddParam("RACER_NO" , edtRacerNo.Value);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	if(fnc_isNull(cboSMbr.Value)) {
        fnc_message(L_COM_ALT_0024, "N", "경정장 구분이");
        return false;
	}

	if(fnc_isNull(calStrDt.Value)) {
        fnc_message(L_COM_ALT_0024, "N", "조회 시작일자가");
        return false;
	}		

	if(fnc_isNull(calEndDt.Value)) {
        fnc_message(L_COM_ALT_0024, "N", "조회 종료일자가");
        return false;
	}		

	if(chkAllRecord.Value != 1 &&  calStrDt.Value > calEndDt.Value) {
        fnc_message(L_COM_ALT_0024, "N", "조회 시작일자가 종료일자보다 먼저일 수");
        return false;
	}		

 	return true;
}


/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회 후
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Index = 0;

        fce_GetRaceTms(cboSStndYear.Value, cboSMbr.Value,"","dsOutSRaceTms");
        fce_GetRaceTms(cboEStndYear.Value, cboSMbr.Value,"","dsOutERaceTms");
		return;
	}

	// 회차 리스트 조회 후
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        if ( cboERaceTms.Index < 0 ) {
            cboERaceTms.Index = 0;
        }

        if ( gF_TmsGubun == "S" ) {
            cboSRaceTms.Index = dsOutSRaceTms.GetRowCount() - 1;
        }

        if ( gF_TmsGubun == "E" ) {
            cboERaceTms.Index = 0;

            if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
                var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
                if ( nIndexRow > -1 ) cboERaceTms.Index = nIndexRow;
            }
        }

        cboSRaceTms_OnChanged();
        cboERaceTms_OnChanged();

       	if(IsExistVar("Arg_preMenuID") && sFirst) {
			chkAllRecord.Value = 1;
			sFirst 			   = false;
			fcsearch();
		}

		return;
	}

	// 조회 후 
	if ( sServiceName == "e02080310-service:searchAllRacerVoilAct" ) {
		fnc_SetStatus(SNIS_BOA_0003, dsRacerVoilAct.GetRowCount());
	}

}


/*-------------------------------------------------------------------------------------------------+
>>  년도변경시
+-------------------------------------------------------------------------------------------------*/
function cboSStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
    fce_GetRaceTms(cboSStndYear.value, cboSMbr.Value, "", "dsOutSRaceTms");
    gF_TmsGubun = "S";
}

/*-------------------------------------------------------------------------------------------------+
>>  년도변경시
+-------------------------------------------------------------------------------------------------*/
function cboEStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fce_GetRaceTms(cboEStndYear.value, cboSMbr.Value, "", "dsOutERaceTms");
    gF_TmsGubun = "E";
}

/*-------------------------------------------------------------------------------------------------+
>>  회차변경시
+-------------------------------------------------------------------------------------------------*/
function cboSRaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
    calStrDt.Value = dsOutSRaceTms.GetColumn(nIndexRow, "STR_DT");
}

/*-------------------------------------------------------------------------------------------------+
>>  회차변경시
+-------------------------------------------------------------------------------------------------*/
function cboERaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var nIndexRow = fnc_GetExistValue(dsOutERaceTms, "TMS", cboERaceTms.Value);
    calEndDt.Value = dsOutERaceTms.GetColumn(nIndexRow, "END_DT");
}



/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}
