function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e03010016-service:searchRacerDetl";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacer=dsOutRacer "
                     + "dsOutRacerFam=dsOutRacerFam "
                     + "dsOutRacerAcad=dsOutRacerAcad "
                     + "dsOutRacerRela=dsOutRacerRela "
                     + "dsOutRacerPrz=dsOutRacerPrz "
                     + "dsOutRacerAward=dsOutRacerAward "
                     + "dsOutRacerIjry=dsOutRacerIjry "
                     + "dsOutRacerSanc=dsOutRacerSanc "
                     + "dsOutRacerFair=dsOutRacerFair ";

	if ( IsExistVar("Arg_sRacerNo") ) {
        fce_AddParam(Trim("RACER_NO     "), Arg_sRacerNo);
        fce_AddParam(Trim("pGBN     "), "002");
        fce_AddParam(Trim("aGBN     "), "001");
        fce_AddParam(Trim("STR_TMS     "), 1);
        fce_AddParam(Trim("END_TMS     "), 100);
    } else {
        fce_AddParam(Trim("RACER_NO     "), edRacerNo.Text);
    }
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRacerFair)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	var rtn_arr;
	var sTemp;
	var asFile;
    for ( i = 0; i < dsOutRacerFair.GetRowCount(); i++ ) {
        if ( dsOutRacerFair.GetColumn(i, "HELMET_UPDATE1") == "Y" ) {
			rtn_arr = fnc_FileWrite( divBtnLst.HttpFile, edHelmetNm1.Text, 
														 dsOutRacerFair.GetColumn(i, "HELMET_PATH1"),
														 "FAIR");

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일1 업로드 에러발생");
				return false;
			}

			sTemp = Replace(rtn_arr[1],"\\","/");
			asFile = split(sTemp, "/");
			dsOutRacerFair.SetColumn(i, "HELMET_PATH1", asFile[asFile.length - 1]);
        }

        if ( dsOutRacerFair.GetColumn(i, "HELMET_UPDATE2") == "Y" ) {
			rtn_arr = fnc_FileWrite( divBtnLst.HttpFile, edHelmetNm2.Text, 
														 dsOutRacerFair.GetColumn(i, "HELMET_PATH2"),
														 "FAIR");

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일2 업로드 에러발생");
				return false;
			}

			sTemp = Replace(rtn_arr[1],"\\","/");
			asFile = split(sTemp, "/");
			dsOutRacerFair.SetColumn(i, "HELMET_PATH2", asFile[asFile.length - 1]);
        }

        if ( dsOutRacerFair.GetColumn(i, "HELMET_UPDATE3") == "Y" ) {
			rtn_arr = fnc_FileWrite( divBtnLst.HttpFile, edHelmetNm3.Text, 
														 dsOutRacerFair.GetColumn(i, "HELMET_PATH3"),
														 "FAIR");

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일3 업로드 에러발생");
				return false;
			}

			sTemp = Replace(rtn_arr[1],"\\","/");
			asFile = split(sTemp, "/");
			dsOutRacerFair.SetColumn(i, "HELMET_PATH3", asFile[asFile.length - 1]);
        }

        if ( dsOutRacerFair.GetColumn(i, "HELMET_UPDATE4") == "Y" ) {
			rtn_arr = fnc_FileWrite( divBtnLst.HttpFile, edHelmetNm4.Text, 
														 dsOutRacerFair.GetColumn(i, "HELMET_PATH4"),
														 "FAIR");

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일4 업로드 에러발생");
				return false;
			}

			sTemp = Replace(rtn_arr[1],"\\","/");
			asFile = split(sTemp, "/");
			dsOutRacerFair.SetColumn(i, "HELMET_PATH4", asFile[asFile.length - 1]);
        }

        if ( dsOutRacerFair.GetColumn(i, "HELMET_UPDATE5") == "Y" ) {
			rtn_arr = fnc_FileWrite( divBtnLst.HttpFile, edHelmetNm5.Text, 
														 dsOutRacerFair.GetColumn(i, "HELMET_PATH5"),
														 "FAIR");

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일5 업로드 에러발생");
				return false;
			}

			sTemp = Replace(rtn_arr[1],"\\","/");
			asFile = split(sTemp, "/");
			dsOutRacerFair.SetColumn(i, "HELMET_PATH5", asFile[asFile.length - 1]);
        }
    }

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

	var sServiceName = "e03010016-service:saveRacerInfoFair";
    var sInDataSet   = "dsOutRacerFair=dsOutRacerFair:u";
    var sOutDataSet  = "dsOutRacer=dsOutRacer dsOutRacerFam=dsOutRacerFam dsOutRacerAcad=dsOutRacerAcad dsOutRacerRela=dsOutRacerRela dsOutRacerFair=dsOutRacerFair ";

	fce_AddParam(Trim("RACER_NO"), edRacerNo.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

