***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
	    // 종교에 '전체' 항목 추가
		fce_AddCodeRow(dsOutRelgn, "전체", "", "");
		fce_AddCodeRow(dsOutArmyClas, "전체", "", "");
		fce_AddCodeRow(dsOutArmyTpe, "전체", "", "");

        if ( IsExistVar("Arg_sRacerNo") ) {
            edRacerNo.Readonly = true;
            fcSearch();     
        }        

    }

	// 조회 후
	if ( sServiceName == "e03010016-service:searchRacerDetl" ) { 
        if ( dsOutRacerFair.GetRowCount() == 0 ) {
            dsOutRacerFair.AddRow();
            dsOutRacerFair.SetColumn(dsOutRacerFair.currow, "RACER_NO", dsOutRacer.GetColumn(dsOutRacer.currow,"RACER_NO"));
        }

        fnc_SetStatus(SNIS_BOA_0003, dsOutRacer.GetRowCount());
	}

	// 저장 후
	if ( sServiceName == "e03010016-service:saveRacerInfoFair" ) {
        //fcSearch();
        dsOutRacerFair_OnRowPosChanged(dsOutRacerFair,0,0); 
        return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

