/*-------------------------------------------------------------------------------------------------+
>>  선택 사원이 변경된 경우 해당 사원의 사진을 보여준다.
+-------------------------------------------------------------------------------------------------*/
function dsOutRacer_OnRowPosChanged(obj,nOldRow,nRow)
{
    this.imgPhotoNm.ImageID = "";
    var sPtoName = dsOutRacer.GetColumn(nRow, "PTO_PATH");

    if ( !fnc_IsNull(sPtoName) ) {
    	//var sPhysicalFileName = split(sPtoName, "\\");
    	var sPhysicalFileName = split(sPtoName, "//");
    	var url = GBL_IMAGELINK_URL + sPhysicalFileName[length(sPhysicalFileName) - 1];

		this.imgPhotoNm.ImageID = url;
		imgPhotoNm.FillType = "STRETCH";
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  선택 사원이 변경된 경우 해당 사원의 장비사진을 보여준다.
+-------------------------------------------------------------------------------------------------*/
function dsOutRacerFair_OnRowPosChanged(obj,nOldRow,nRow)
{
    var sHelmetName = "";
    var sPhysicalFileName = "";
    var url = "";

    this.imgHelmetNm1.ImageID = "";
    this.imgHelmetNm2.ImageID = "";
    this.imgHelmetNm3.ImageID = "";
    this.imgHelmetNm4.ImageID = "";
    this.imgHelmetNm5.ImageID = "";

    sHelmetName = dsOutRacerFair.GetColumn(nRow, "HELMET_PATH1");
    if ( !fnc_IsNull(sHelmetName) ) {
    	sPhysicalFileName = split(sHelmetName, "//");
    	url = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName[length(sPhysicalFileName) - 1];

		this.imgHelmetNm1.ImageID = url;
		imgHelmetNm1.FillType = "STRETCH";
	}

    sHelmetName = dsOutRacerFair.GetColumn(nRow, "HELMET_PATH2");
    if ( !fnc_IsNull(sHelmetName) ) {
    	sPhysicalFileName = split(sHelmetName, "//");
    	url = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName[length(sPhysicalFileName) - 1];

		this.imgHelmetNm2.ImageID = url;
		imgHelmetNm2.FillType = "STRETCH";
	}

    sHelmetName = dsOutRacerFair.GetColumn(nRow, "HELMET_PATH3");
    if ( !fnc_IsNull(sHelmetName) ) {
    	sPhysicalFileName = split(sHelmetName, "//");
    	url = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName[length(sPhysicalFileName) - 1];

		this.imgHelmetNm3.ImageID = url;
		imgHelmetNm3.FillType = "STRETCH";
	}

    sHelmetName = dsOutRacerFair.GetColumn(nRow, "HELMET_PATH4");
    if ( !fnc_IsNull(sHelmetName) ) {
    	sPhysicalFileName = split(sHelmetName, "//");
    	url = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName[length(sPhysicalFileName) - 1];

		this.imgHelmetNm4.ImageID = url;
		imgHelmetNm4.FillType = "STRETCH";
	}

    sHelmetName = dsOutRacerFair.GetColumn(nRow, "HELMET_PATH5");
    if ( !fnc_IsNull(sHelmetName) ) {
    	sPhysicalFileName = split(sHelmetName, "//");
    	url = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName[length(sPhysicalFileName) - 1];

		this.imgHelmetNm5.ImageID = url;
		imgHelmetNm5.FillType = "STRETCH";
	}
}

