function fcImgDialoge(obj,nX,nY) {
	var sArg = "";
	var sImgNm = "";

	switch(obj.id) {
		case "imgPhotoNm" :   //선수사진
			sImgNm = dsOutRacer.GetColumn(dsOutRacer.currow, "PTO_PATH");
			if ( fnc_IsNull(sImgNm) ) return;

			sArg = "Arg_Gubun=Racer Arg_imgNm=" + sImgNm;
			break;
		case "imgHelmetNm1" :   //헬멧사진1
			sImgNm = dsOutRacerFair.GetColumn(dsOutRacerFair.currow, "HELMET_PATH1");
			if ( fnc_IsNull(sImgNm) ) return;

			sArg = "Arg_Gubun=Fair Arg_imgNm=" + sImgNm;
			break;
		case "imgHelmetNm2" :   //헬멧사진2
			sImgNm = dsOutRacerFair.GetColumn(dsOutRacerFair.currow, "HELMET_PATH2");
			if ( fnc_IsNull(sImgNm) ) return;

			sArg = "Arg_Gubun=Fair Arg_imgNm=" + sImgNm;
			break;
		case "imgHelmetNm3" :   //헬멧사진3
			sImgNm = dsOutRacerFair.GetColumn(dsOutRacerFair.currow, "HELMET_PATH3");
			if ( fnc_IsNull(sImgNm) ) return;

			sArg = "Arg_Gubun=Fair Arg_imgNm=" + sImgNm;
			break;
		case "imgHelmetNm4" :   //헬멧사진4
			sImgNm = dsOutRacerFair.GetColumn(dsOutRacerFair.currow, "HELMET_PATH4");
			if ( fnc_IsNull(sImgNm) ) return;

			sArg = "Arg_Gubun=Fair Arg_imgNm=" + sImgNm;
			break;
		case "imgHelmetNm5" :   //헬멧사진5
			sImgNm = dsOutRacerFair.GetColumn(dsOutRacerFair.currow, "HELMET_PATH5");
			if ( fnc_IsNull(sImgNm) ) return;

			sArg = "Arg_Gubun=Fair Arg_imgNm=" + sImgNm;
			break;
	}

	Dialog("form::E03010013.xml", sArg, 0, 0, true);
}
