/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    if (tab0.tabindex == 0) {
		dsOutRacerArrange.ClearData();
		oLastDs       = dsOutRacerArrange;
    } else {
        dsOutRacerArrange_0.ClearData();
		oLastDs     = dsOutRacerArrange_0;
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e03040010-service:searchEquipmentArrange";
    var sInDataSet   = "";
    var sOutDataSet  = "";

	if (tab0.TabIndex == 0) {
        sOutDataSet  = "dsOutRacerArrange=dsOutRacerArrange  "
                     + "dsOutPrtSeq=dsOutPrtSeq ";
		fce_AddParam(Trim("EXCL_YN"), "N");
	} else {
        sOutDataSet  = "dsOutRacerArrange_0=dsOutRacerArrange  "
                     + "dsOutPrtSeq_0=dsOutPrtSeq ";
	 	fce_AddParam(Trim("EXCL_YN"), "Y");
	}

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD"), cboSMbr.Value);
	fce_AddParam(Trim("TMS"), cboSRaceTms.Value);
	fce_AddParam(Trim("NM_KOR"), edRacerNm.Text);

 	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRacerArrange) && !fnc_DatasetChangeCheck(dsOutRacerArrange_0))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	if (tab0.TabIndex == 0) {
		// 필수항목체크
		oDataSet          = dsOutRacerArrange;
		sMandatoryColID   = "MOT_NO|BOAT_NO";
		sMandatoryColName = "모터번호|보트번호";
		oGrid             = "tab0.tab1.grdEquip";
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}
	} else {
		// 필수항목체크
		oDataSet          = dsOutRacerArrange_0;
		sMandatoryColID   = "MOT_NO|BOAT_NO";
		sMandatoryColName = "모터번호|보트번호";
		oGrid             = "tab0.tab2.grdEquip";
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e03040010-service:SaveEquipmentArrange";
    var sInDataSet   = "dsOutRacerArrange=dsOutRacerArrange:u dsOutRacerArrange_0=dsOutRacerArrange_0:u ";
    var sOutDataSet  = "dsOutRacerArrange=dsOutRacerArrange dsOutRacerArrange_0=dsOutRacerArrange_0";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

