>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
	var grdNm = "";
	if (tab0.tabindex == 0) grdNm = "tab0.tab1.grdEquip";
	else                    grdNm = "tab0.tab2.grdEquip";
	fnc_OpenExcelExport(grdNm);         //입력 첫 항목으로 Focus
}

