/*-------------------------------------------------------------------------------------------------+
>>  추첨랜덤출력 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function btnPrintRandom_OnClick(obj)
{
	var sArg = "";
	sArg += "STND_YEAR="+ quote(cboSStndYear.Value);
	sArg += " MBR_CD="   + quote(cboSMbr.Value);
	sArg += " TMS="      + quote(cboSRaceTms.Value);
	if (tab0.TabIndex == 0) sArg += " EXCL_YN="      + quote("N");
	else                    sArg += " EXCL_YN="      + quote("Y");


	Open("form::E03040012.xml", sArg, 306, 168, "Resize=false");
	return;

	var objDsArrange;
	var sExclYn = "N";
	if (tab0.TabIndex == 0) {
		objDsArrange = dsOutRacerArrange;
	} else  {
		objDsArrange = dsOutRacerArrange_0;
		sExclYn = "Y";
	}
	objDsArrange.ClearData();
    dsOutRacerArrange_rpt.ClearData();
    dsOutRacerArrange_tmp.ClearData();

    http.sync = true;
    fcSearchReportData();	
	http.sync = false;

    if (dsOutRacerArrange_rpt.RowCount() > 0) {
       var ARRAY_LENGTH = dsOutRacerArrange_rpt.GetRowCount(); 

       var chk = Array(ARRAY_LENGTH); 
       for ( var j = 0; j < ARRAY_LENGTH; j++ ) {                 
		  chk[j] = 999; //초기값 
	   }    
		//추출 수 
	   var icnt = 0;   
	   while ( icnt<=ARRAY_LENGTH){
			//난수 발생
			var iRnd = Random(0, ARRAY_LENGTH); 
			if (chk[iRnd] == 999) { //초기값 Index만 추출한다.
				icnt++;          //카운트를 하나 늘림
				chk[iRnd] = 1;            //선택된 배열의 값을 1로 세팅
				dsOutRacerArrange_tmp.AddRow();

				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "SEQ", icnt);
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "STND_YEAR", dsOutRacerArrange_rpt.GetColumn(iRnd, "STND_YEAR"));
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "MBR_CD", dsOutRacerArrange_rpt.GetColumn(iRnd, "MBR_CD"));
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "TMS", dsOutRacerArrange_rpt.GetColumn(iRnd, "TMS"));
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "RACER_NO", dsOutRacerArrange_rpt.GetColumn(iRnd, "RACER_NO"));
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "NM_KOR", dsOutRacerArrange_rpt.GetColumn(iRnd, "NM_KOR"));
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "BOAT_NO", "");
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "MOT_NO", "");
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "ETC", dsOutRacerArrange_rpt.GetColumn(iRnd, "ETC"));
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "ENTRY_BODY_WGHT", dsOutRacerArrange_rpt.GetColumn(iRnd, "ENTRY_BODY_WGHT"));
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "RACER_GRD_CD", dsOutRacerArrange_rpt.GetColumn(iRnd, "RACER_GRD_CD"));
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "HEAL_STAT_CD", dsOutRacerArrange_rpt.GetColumn(iRnd, "HEAL_STAT_CD"));
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "RACER_GRD_CD_NM", dsOutRacerArrange_rpt.GetColumn(iRnd, "RACER_GRD_CD_NM"));
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "HEAL_STAT_CD_NM", dsOutRacerArrange_rpt.GetColumn(iRnd, "HEAL_STAT_CD_NM"));
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "VIEW_MOT_NO", "");
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "VIEW_BOAT_NO", "");				
				dsOutRacerArrange_tmp.SetColumn(dsOutRacerArrange_tmp.rowposition, "EXCL_YN", dsOutRacerArrange_rpt.GetColumn(iRnd, "EXCL_YN"));
				if(icnt == ARRAY_LENGTH)break;
			} 
		 }
    }
    objDsArrange.ClearData();
    dsOutRacerArrange_rpt.ClearData();
    objDsArrange.Copy(dsOutRacerArrange_tmp);
    dsOutRacerArrange_rpt.Copy(dsOutRacerArrange_tmp);

    fcRandomSave();
}


