/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Index = 0;
        cboSMbr.Value = fce_GetUserInfo("MBR_CD");

        fce_GetRaceTms(cboSStndYear.Value,cboSMbr.Value,"","dsOutSRaceTms");
		return;
	}

	// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        cboSRaceTms.Index = 0;
        if ( cboSStndYear.Value == CURR_STND_YEAR ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", CURR_TMS);
            if ( nIndexRow > -1 ) cboSRaceTms.Index = nIndexRow;
        }       

        //조회
        fcSearch();
		return;
	}

	// 조회 후
	if ( sServiceName == "e03040010-service:searchEquipmentArrange" ) {
        if ( dsOutRacerArrange.GetRowCount() == 0 ) {
        }

        fnc_SetStatus(SNIS_BOA_0003, dsOutRacerArrange.GetRowCount());

        var nRow = fnc_GetExistValue(dsOutSRaceTms, "Tms", cboSRaceTms.Value);

        if ( sInpt == "T" ) { //저장권한 있는 사용자에 한하여
             if ( dsOutSRaceTms.GetColumn(nRow, "RACE_TMS_STAT_CD") < "020" ) { //모터보트 배정이 확정되기 전이라면
                this.divBtnLst.btnSave.Enable  = true;
                tab0.tab1.btnPrintRandom.Enable = true;
                tab0.tab2.btnPrintRandom.Enable = true;
                tab0.tab1.btnRandomResult.Enable = true;
                tab0.tab2.btnRandomResult.Enable = true;
                btnConfirm.Enable = true;
                btnConfirm.Visible = true;
                btnUnConfirm.Visible = false;
             }else{
                this.divBtnLst.btnSave.Enable  = false;
                /*
                tab0.tab1.btnPrintRandom.Enable = false;
                tab0.tab2.btnPrintRandom.Enable = false;
                tab0.tab1.btnPrintRandom.Enable = false;
                tab0.tab2.btnPrintRandom.Enable = false;
                tab0.tab1.btnPrintRandom.Enable = false;
                tab0.tab2.btnPrintRandom.Enable = false;
                */
                tab0.tab1.btnRandomResult.Enable = false;
				tab0.tab2.btnRandomResult.Enable = false;
                btnConfirm.Enable = false;
				btnUnConfirm.Visible = true;
				btnConfirm.Visible = false;

                if( dsOutSRaceTms.GetColumn(nRow, "RACE_TMS_STAT_CD") < "050") {
					btnUnConfirm.Enable = true;
				} else {
					btnUnConfirm.Enable = false;
				}
             }
        }

		return;
	}

	// 저장 후
	if ( sServiceName == "e03040010-service:SaveEquipmentArrange" || sServiceName == "e03040010-service:SaveEquipmentConfirm" || sServiceName == "e03040010-service:SaveEquipmentUnConfirm") {        
        //공통코드 조회
        fce_GetComboDs(dsInComCd);

        fcSearch();
		return;
	}

	// 추첨랜덤 저장 후
	if ( sServiceName == "e03040010-service:SaveEquipmentRandom") {        

        var logImgUrl = GBL_REPORT_URL+"log.jpg";
        var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
        var sRptNm    = "e03049010_racer_equip.jrf";               //레포트 파일명
        var sDataSet  = "racer_equip=dsOutRacerArrange_rpt";    //"UBI 쿼리명, MIP Dataset명"

        var sArg  = " STND_YEAR="+cboSStndYear.Value; 
            sArg += " TMS="+cboSRaceTms.Value;
            sArg += " IMG_URL="+logImgUrl; 
        var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

        fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
		return;
	}		    

    //모터/보트 
	if ( sServiceName == "e03040010-service:searchEquipmentMotLot") {        
		if (tab0.tabindex == 0) {
			if ( dsOutMotLot.GetRowCount() == 0 || dsOutBoatLot.GetRowCount() == 0 ) {
				fnc_Message(SNIS_BOA_C004);
				return;
			}
		} else {
			sOutDataSet  = "dsOutMotLot_0=dsOutMotLot dsOutBoatLot_0=dsOutBoatLot"; 		//추첨 대상 보트 
			if ( dsOutMotLot_0.GetRowCount() == 0 || dsOutBoatLot_0.GetRowCount() == 0 ) {
				fnc_Message(SNIS_BOA_C004);
				return;
			}
		}
    }	

	// 예비장비조회 후
	if ( sServiceName == "e03050010-service:searchEquipMot" ) {

        var logImgUrl = GBL_REPORT_URL + "log.jpg";
        var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
        var sRptNm    = "e03059010_equip.jrf";               //레포트 파일명
        var sDataSet  = "mot=dsOutMot "; //"UBI 쿼리명, MIP Dataset명"
            sDataSet += "boat=dsOutBoat ";

        var sArg      = " IMG_URL="+logImgUrl;
            sArg     += " STND_YEAR="+cboSStndYear.Value;
            sArg     += " TMS="+cboSRaceTms.Value;

        var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

        fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);

        return;
	}	

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

