/*-------------------------------------------------------------------------------------------------+
>>  장비 리스트 Double Click시 각 장비에 해당 장비 등록
+-------------------------------------------------------------------------------------------------*/
function fcGrdEquipAdd(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    var srcColumn = "";
    if ( obj.ID == "grdMot")  {
        srcColumn   = "MOT_NO"; 
        srcDataSet  = dsOutMotLot;   
        if (tab0.tabindex == 1) srcDataSet  = dsOutMotLot_0;        
    }
    if ( obj.ID == "grdBoat")  {
        srcColumn = "BOAT_NO"; 
        srcDataSet  = dsOutBoatLot;
        if (tab0.tabindex == 1) srcDataSet  = dsOutBoatLot_0;        
    }

    //예비모터/보트일 경우는 배정되지 않도록.
    if (srcDataSet.GetColumn(nRow,"PREPAR_YN") == 'Y') return;        

    var sEquipNo = object(obj.BindDataset).getColumn(nRow, srcColumn);
    if ( fnc_IsNull(sEquipNo) ) return;

    if ( !fnc_IsNull(oLastDs.GetColumn(oLastDsRow, srcColumn)) ) return;

    oLastDs.row = oLastDsRow;    
    MoveEquipAllocation(oLastDs, oLastDsRow, srcDataSet, nRow, nCell);

	// 2010.5.29 수정(확정이후 주선변경선수 재입력시 입력이 안되었음)
    oLastDsRow++;
}

/*-------------------------------------------------------------------------------------------------+
>>  각 장비 클릭 시 최종 그리드 
+-------------------------------------------------------------------------------------------------*/
function fcgrdEquip_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    oLastDs       = object(obj.BindDataset);
    if ( nRow < 0 ) nRow = 0;
    oLastDsRow    = nRow;	
}

/*-------------------------------------------------------------------------------------------------+
>>  경주선수 Double Click 경주선수 삭제, 선수 리스트에 추가
+-------------------------------------------------------------------------------------------------*/
function fcgrdEquip_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	oLastDs       = object(obj.BindDataset);
    oLastDsRow    = nRow;
    var sColumnText = "";    
    var sColumnID   = "";
    var sExclYn    = "";

    if (tab0.TabIndex == 0) {
		sColumnText = tab0.tab1.grdEquip.GetCellText("Head", nRow, nCell);
	} else {
		sColumnText = tab0.tab2.grdEquip.GetCellText("Head", nRow, nCell);
	}

    if ( nCell == 0 || nCell == 1 )  {
		var oDsList = null;
		if (tab0.TabIndex == 0) {
			oDsList = dsOutRacerArrange;
			sExclYn = "N";
		} else {
		    oDsList = dsOutRacerArrange_0;
		    sExclYn = "Y";
		}

        if ( fnc_IsNull(oDsList.GetColumn(nRow, "ORG_RACER_NO")) ) return;

        var sArg  = " Arg_sStndYear=" + quote(cboSStndYear.Value);
            sArg += " Arg_sMbrCd="    + quote(cboSMbr.Value     );
            sArg += " Arg_sTms="      + quote(cboSRaceTms.Value );
            sArg += " Arg_sExclYn="   + quote(sExclYn);

        var bReturn = Dialog("form::E03040011.xml", sArg, 250, 250, "");

        if ( bReturn == false || fnc_Isnull(bReturn) ) return;

        oDsList.SetColumn(nRow, "RACER_NO", bReturn[0]);
        oDsList.SetColumn(nRow, "NM_KOR",   bReturn[1]);

        return;
    }
    if ( nCell == 5 )  {
        sColumnID   = "MOT_NO";
		if (tab0.TabIndex == 0) srcDataSet  = dsOutMotLot;
		else                    srcDataSet  = dsOutMotLot_0;
    }
    if ( nCell == 6 )  {
        sColumnID   = "BOAT_NO";
        if (tab0.TabIndex == 0) srcDataSet  = dsOutBoatLot;
        else                    srcDataSet  = dsOutBoatLot_0;
    }

    var sEquipNo = oLastDs.getColumn(nRow, sColumnID);
    if ( fnc_IsNull(sEquipNo) ) return;

    srcDataSet.AddRow();
    MoveEquipAllocation(srcDataSet, srcDataSet.currow, oLastDs, oLastDsRow, nCell);
}


/*-------------------------------------------------------------------------------------------------+
>>  모터/보트 세팅
+-------------------------------------------------------------------------------------------------*/
function MoveEquipAllocation(objTarget, nRowTarget, objSource, nRowSource, nCellSource)
{
	if (objTarget.rowcount() <= nRowTarget) return;

    var sColumnText = "";
    if (tab0.tabindex == 0) sColumnText = tab0.tab1.grdEquip.GetCellText("Head", nRowSource, nCellSource);
    else                    sColumnText = tab0.tab2.grdEquip.GetCellText("Head", nRowSource, nCellSource);

    for ( var i = 0; i < objSource.GetColCount(); i++ ) {
        var sColumnID = objSource.GetColID(i);
        var srcColumn = "";

        //팝업창에서 장비세팅할때
        if ( objSource.ID == "dsOutMotLot"  || objSource.ID == "dsOutMotLot_0")  {
            srcColumn   = "MOT_NO";  
        }
        if ( objSource.ID == "dsOutBoatLot" || objSource.ID == "dsOutBoatLot_0")  {
            srcColumn = "BOAT_NO"; 
        }

        //모터보트배정에서 팝업으로 다시 이동
        if ( objSource.ID == "dsOutRacerArrange" || objSource.ID == "dsOutRacerArrange_0")  {
            if ( sColumnText == "모터번호" ) srcColumn   = "MOT_NO";
            if ( sColumnText == "보트번호" ) srcColumn   = "BOAT_NO"; 
        }        
        if ( srcColumn != null && sColumnID != null && sColumnID == srcColumn ) {
            //첫번째 행이 비어있으면 포커스를 첫번째 행으로..
            /*if (fnc_IsNull(objTarget.GetColumn(0, sColumnID))) {
                nRowTarget = 0; 
                objTarget.Row =  nRowTarget;                             
            }*/                
            //값을 세팅
            objTarget.SetColumn(nRowTarget, srcColumn, objSource.GetColumn(nRowSource, sColumnID));
            objSource.SetColumn(nRowSource, sColumnID, "");
            //예비여부세팅
            objTarget.SetColumn(nRowTarget, "PREPAR_YN", "N");
            objSource.SetColumn(nRowSource, sColumnID, "");        
        }
    }

    if ( objSource.ID == "dsOutMotLot"  || objSource.ID == "dsOutMotLot_0"  || objSource.ID == "dsOutMotLotTemp") objSource.DeleteRow(nRowSource);
    if ( objSource.ID == "dsOutBoatLot" || objSource.ID == "dsOutBoatLot_0" || objSource.ID == "dsOutBoatLotTemp") objSource.DeleteRow(nRowSource);
}

/*-------------------------------------------------------------------------------------------------+
>>  각 장비(모터/보트) 드레그
+-------------------------------------------------------------------------------------------------*/
function fcEquipDrag(obj,objDragObj,objDataObj,nPosX,nPosY,varKeyState,nRow,nCell)
{
    var dataObj = CreateDataObject();
	dataObj.SetData("MOT_TEXT", obj.ID);
	dataObj.SetData("BOAT_TEXT", obj.ID);

	if ( obj.IsAboveSel() ) return dataObj;	
}


/*-------------------------------------------------------------------------------------------------+
>>  각 장비(모터/보트) 드롭시
+-------------------------------------------------------------------------------------------------*/
function fcEquipDrop(obj,objDragObj,objDataObj,nPosX,nPosY,varKeyState,nRow,nCell)
{
    var objName       = "grdMot";

	var sTargetMotNo = substr(obj.Id, length(objName));
    var objTargetDs   = dsOutRacerArrange;

	if ( objDataObj.GetData("MOT_TEXT") == "grdMot" ) // MultiLine Grid 에서 온경우
	{
        if ( nRow < 0 ) return;
        MoveEquipAllocation(objTargetDs, nRow, dsOutRacerArrange, dsOutRacerArrange.currow);
        objTargetDs.row = nRow;

        oLastDs       = objTargetDs;
        oLastDsRow    = nRow;

        return;
	}

	var sSourceMotNo = substr(objDataObj.GetData("MOT_TEXT"), length(objName));
    var objSourceDs   = Object("dsOutRacerArrange" + sSourceMotNo);

    if ( nRow < 0 ) {
        oLastDs       = objTargetDs;
        oLastDsRow    = objSourceDs.currow;

        dsOutRacerArrange.AddRow();
        MoveMotAllocation(dsOutRacerArrange, dsOutRacerArrange.currow, oLastDs, oLastDsRow);
    } else {
        dsOutRacerArrangeTemp.AddRow();
        MoveEquipAllocation(dsOutRacerArrangeTemp, dsOutRacerArrangeTemp.currow, objTargetDs, nRow);
        MoveEquipAllocation(objTargetDs, nRow, objSourceDs, objSourceDs.currow);
        MoveEquipAllocation(objSourceDs, objSourceDs.currow, dsOutRacerArrangeTemp, dsOutRacerArrangeTemp.currow);

        oLastDs         = objTargetDs;
        oLastDsRow      = nRow;
        objTargetDs.row = nRow;
    }
}

//  모터/보트 동시선택 방지
function dsOutRacerArrange_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "MOT_NO" || strColumnID == "BOAT_NO"){
        var sCompValue = "";
        var sCurrValue = "";

        for ( var i = 0; i < obj.GetRowCount(); i++ ) {
            //팝업창에서 장비세팅할때
            if ( i != nRow ) {
                if ( strColumnID == "MOT_NO")  {
                    sCompValue = obj.GetColumn(i, "MOT_NO");
                    sCurrValue = obj.GetColumn(nRow, "MOT_NO");
                    srcDataSet   = dsOutMotLot;  
                }
                if ( strColumnID == "BOAT_NO")  {
                    sCompValue = obj.GetColumn(i, "BOAT_NO");
                    sCurrValue = obj.GetColumn(nRow, "BOAT_NO");
                    srcDataSet = "dsOutBoatLot";
                }

                //이미 선택된 값인지를 비교
                if ( sCompValue != null && sCurrValue != null ) {
                    if (sCompValue != "" && sCurrValue != "" && sCompValue == sCurrValue ) {                      
                        fnc_Message(SNIS_BOA_C002, "N", obj.GetColumn(i, "NM_KOR"), SubStr(sCurrValue,5,3));
                        obj.SetColumn(nRow, strColumnID, "");                    
                    }
                }
            }
        }
	}	
}

