/*-------------------------------------------------------------------------------------------------+
>>  추첨결과입력 버튼 클릭 時 랜덤값 가져오기
+-------------------------------------------------------------------------------------------------*/
function btnRandomResult_OnClick(obj)
{
	var sArg = "";
	sArg += "STND_YEAR="+ quote(cboSStndYear.Value);
	sArg += " MBR_CD="   + quote(cboSMbr.Value);
	sArg += " TMS="      + quote(cboSRaceTms.Value);
	if (tab0.TabIndex == 0) sArg += " EXCL_YN="      + quote("N");
	else                    sArg += " EXCL_YN="      + quote("Y");

	Open("form::E03040013.xml", sArg, 306, 168, "Resize=false");
	return;

    //MAX PRT_SEQ를 기준으로 난수발생하여 랜덤출력저장 값을 가져옴
	var sServiceName = "e03040010-service:searchRandomResult";
    var sInDataSet   = "";
    var sOutDataSet  = "";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD"), cboSMbr.Value);
	fce_AddParam(Trim("TMS"), cboSRaceTms.Value);

	PRT_SEQ ++;
	var PrtSeq = 0;
	if (tab0.TabIndex == 0) {
		sOutDataSet  = "dsOutRacerArrange=dsOutRacerArrange  ";
		fce_AddParam("EXCL_YN", "N");
		if (PRT_SEQ >= dsOutPrtSeq.rowcount()) PRT_SEQ = 0;
		PrtSeq = dsOutPrtSeq.GetColumn(PRT_SEQ, "PRT_SEQ");
	} else {
		sOutDataSet  = "dsOutRacerArrange_0=dsOutRacerArrange  ";
	 	fce_AddParam("EXCL_YN", "Y");
		if (PRT_SEQ >= dsOutPrtSeq_0.rowcount()) PRT_SEQ = 0;
		PrtSeq = dsOutPrtSeq_0.GetColumn(PRT_SEQ, "PRT_SEQ");
	}

    fce_AddParam(Trim("PRT_SEQ"), PrtSeq);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

