function Tab0_OnChanged(obj,nOldIndex,nNewindex)
{
	if (nNewindex == 1 && dsOutRacerArrange_0.RowCount() == 0) {
		fcSearch(obj);
	}
	if (nNewIndex == 0) {
		oLastDs     = dsOutRacerArrange;
		dsOutRacerArrange.row = 0;
	} else {               
		oLastDs     = dsOutRacerArrange_0;
		dsOutRacerArrange_0.row = 0;
	}
	oLastDsRow = 0;
}
