/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
    sBUTTONLIST   = 'TFFTFT';
	if(sInpt == "F"){
		//this.divBtnLst.btnSave.Visible = false;
		btnReqIssue.Visible = false;
	}

	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	http.sync= true;

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

    //년도조회
    fce_SetStndYear(dsOutSIssuYear, toInteger(fce_GetSysdate("YYYY")));
    cboSIssuYear.Index = 0;

	//공통코드 조회
	fce_GetComboDs(dsInComCd);

	http.sync= false; 
}

