//조회 
function fcSearch(obj) {
	edRacerNm.Text = "";
	edRacerNo.Text = "";
	edIssuCnt.Text = "1";
	cboPurposes.Index = "0";

	var sServiceName = "e03110010-service:searchCertIssu";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCertIssu=dsOutCertIssu  ";

	fce_AddParam(Trim("CERT_KND_CD"), "001"); //선수증명서
	fce_AddParam(Trim("ISSU_YEAR"), cboSIssuYear.Value);
	fce_AddParam(Trim("PURPOSES_CD"), cboSPurposes.Value);
	fce_AddParam(Trim("NM_KOR"), sRacerNm.Text);
    fce_AddParam(Trim("ISSU_DTM"), calIssuDt.Value);
    fce_AddParam(Trim("REQ_TYPE"), "1");

 	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutCertIssu       ) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutCertIssu;
	sMandatoryColID   = "PURPOSES_CD|RACER_NO";
	sMandatoryColName = "용도|선수명";
	oGrid             = "grdMaster";
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}	
	return true;

}

function fcSave(obj) {
    if ( !fcSaveCheck(obj) ) return;

    var sServiceName = "e03110010-service:SaveCertIssu";
    var sInDataSet   = "dsOutCertIssu=dsOutCertIssu:u";
    var sOutDataSet  = "dsOutCertIssu=dsOutCertIssu";

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

