/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	if(dsOutCertIssu.getColumn(dsOutCertIssu.rowposition, 'PRS_STAT_CD')=='002'){
		if ( dsOutCertIssu.getColumn(dsOutCertIssu.rowposition, 'ISSU_YN') =='발급'){
			fnc_Message(SNIS_BOA_G014);
            return;
        }
	}

    http.sync = true;
    fcSearchReportData();
	http.sync = false;

    var issu_year   = dsOutCertIssu.getColumn(dsOutCertIssu.rowposition, 'ISSU_YEAR'); 
    var issu_no     = dsOutCertIssu.getColumn(dsOutCertIssu.rowposition, 'ISSU_NO'); 
    var issu_seq    = dsOutCertIssu.getColumn(dsOutCertIssu.rowposition, 'SEQ');
    var racer_no    = dsOutCertIssu.getColumn(dsOutCertIssu.rowposition, 'RACER_NO');
    var racer_nm    = dsOutCertIssu.getColumn(dsOutCertIssu.rowposition, 'NM_KOR');
    var issu_cnt    = dsOutCertIssu.getColumn(dsOutCertIssu.rowposition, 'ISSU_CNT');
    var max_no      = dsOutCertIssu_rpt.getColumn(dsOutCertIssu_rpt.rowposition, 'MAXNO');
    var nm_chn      = dsOutCertIssu_rpt.getColumn(dsOutCertIssu_rpt.rowposition, 'NM_CHN');
    var res_no      = dsOutCertIssu_rpt.getColumn(dsOutCertIssu_rpt.rowposition, 'RES_NO');
    var post_no     = dsOutCertIssu_rpt.getColumn(dsOutCertIssu_rpt.rowposition, 'POST_NO');
    var add1_home   = dsOutCertIssu_rpt.getColumn(dsOutCertIssu_rpt.rowposition, 'ADD1_HOME');
    var add2_home   = dsOutCertIssu_rpt.getColumn(dsOutCertIssu_rpt.rowposition, 'ADD2_HOME');
    var perio_term  = dsOutCertIssu_rpt.getColumn(dsOutCertIssu_rpt.rowposition, 'PERIO_TERM');
    var purposes_cd_nm = dsOutCertIssu_rpt.getColumn(dsOutCertIssu_rpt.rowposition, 'PURPOSES_CD_NM');

    dsOutCertIssu.setColumn(dsOutCertIssu.rowposition, 'ISSU_YN', 'Y');	
	dsOutCertIssu.setColumn(dsOutCertIssu.rowposition, 'ISSU_DTM', CURR_DT);
    dsOutCertIssu.setColumn(dsOutCertIssu.rowposition, 'ISSU_NO', max_no+issu_cnt);

	fcSavePrntCheck();

	//데이터 검색 
	dsOutCertIssu_rpt.ClearData();
	for(i=0; i<issu_cnt; i++){
		dsOutCertIssu_rpt.AddRow();
		var report_issu_no = max_no + i+1;
		dsOutCertIssu_rpt.SetColumn(dsOutCertIssu_rpt.rowposition, 'ISSU_YEAR', issu_year);
		dsOutCertIssu_rpt.SetColumn(dsOutCertIssu_rpt.rowposition, 'ISSU_NO', report_issu_no);
		dsOutCertIssu_rpt.SetColumn(dsOutCertIssu_rpt.rowposition, 'RACER_NO', racer_no);		
		dsOutCertIssu_rpt.SetColumn(dsOutCertIssu_rpt.rowposition, 'NM_KOR', racer_nm);
		dsOutCertIssu_rpt.SetColumn(dsOutCertIssu_rpt.rowposition, 'NM_CHN', nm_chn);
		dsOutCertIssu_rpt.SetColumn(dsOutCertIssu_rpt.rowposition, 'RES_NO', res_no);
		dsOutCertIssu_rpt.SetColumn(dsOutCertIssu_rpt.rowposition, 'POST_NO', post_no);
		dsOutCertIssu_rpt.SetColumn(dsOutCertIssu_rpt.rowposition, 'ADD1_HOME', add1_home);
		dsOutCertIssu_rpt.SetColumn(dsOutCertIssu_rpt.rowposition, 'ADD2_HOME', add2_home);
		dsOutCertIssu_rpt.SetColumn(dsOutCertIssu_rpt.rowposition, 'PERIO_TERM', perio_term);
		dsOutCertIssu_rpt.SetColumn(dsOutCertIssu_rpt.rowposition, 'PURPOSES_CD_NM', purposes_cd_nm);
	}

    var sealmgUrl = GBL_REPORT_URL+"seal2.jpg"; 
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e03119010_racer_cert.jrf";               //레포트 파일명
	var sDataSet  = "racer_cert=dsOutCertIssu_rpt";    //"UBI 쿼리명, MIP Dataset명"

	var sArg  = " IMG_URL="+logImgUrl;
        sArg += " SEAL_IMG_URL="+ sealmgUrl;

	var sRptDrct    = 1;           //출력방향(0 : 수직, 1 : 수평)
	//var sPrintCnt   = issu_cnt;    //인쇄매수 설정

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, 1, 1);
}

/*-------------------------------------------------------------------------------------------------+
>>  레포트를 위한 선수증명서 정보 가져오기
+-------------------------------------------------------------------------------------------------*/
function fcSearchReportData(obj) {
	var sServiceName = "e03110010-service:searchCertIssu_rpt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCertIssu_rpt=dsOutCertIssu_rpt";

	fce_AddParam(Trim("RACER_NO"), dsOutCertIssu.GetColumn(dsOutCertIssu.rowposition, "RACER_NO"));
	fce_AddParam(Trim("ISSU_YEAR"), dsOutCertIssu.GetColumn(dsOutCertIssu.rowposition, "ISSU_YEAR"));
	fce_AddParam(Trim("SEQ"), dsOutCertIssu.GetColumn(dsOutCertIssu.rowposition, "SEQ"));
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

