/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        fce_AddCodeRow(dsOutPurposes, "전체", "", cboSPurposes);
        fcInit();
		return;
	}	

	// 조회 후
	if ( sServiceName == "e03110010-service:searchCertIssu" ) {
	    fnc_SetStatus(SNIS_BOA_0003,  dsOutCertIssu.GetRowCount());

	}

	// 저장 후
	if ( sServiceName == "e03110010-service:SaveCertIssu" || sServiceName == "e03110010-service:SaveCertPrintCheck" ) {
		fcSearch();
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProceS(sServiceName);
}

