>>  인쇄여부 저장
+-------------------------------------------------------------------------------------------------*/
function fcSavePrntCheck(obj) {
    var sServiceName = "e03110010-service:SaveCertPrintCheck";
    var sInDataSet   = "dsOutCertIssu=dsOutCertIssu:u";
    var sOutDataSet  = "dsOutCertIssu=dsOutCertIssu";

    http.sync = true;
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
    http.sync = false;
}

function btnReqIssue_OnClick(obj)
{
	if(!fcAccCheck()){
		return;
	}
	dsOutCertIssu.InsertRow(0);

	//dsOutCertIssu.setColumn(dsOutCertIssu.rowposition, 'ISSU_YEAR', CURR_STND_YEAR);
	dsOutCertIssu.setColumn(dsOutCertIssu.rowposition, 'USER_ID', gdsUser.GetColumn(gdsUser.rowposition, "USER_ID"));
	dsOutCertIssu.setColumn(dsOutCertIssu.rowposition, 'USER_NM', gdsUser.GetColumn(gdsUser.rowposition, "USER_NM"));
	dsOutCertIssu.setColumn(dsOutCertIssu.rowposition, 'ISSU_YEAR', edIssuYear.Text);
	dsOutCertIssu.setColumn(dsOutCertIssu.rowposition, 'ISSU_CNT', edIssuCnt.Text);
	dsOutCertIssu.setColumn(dsOutCertIssu.rowposition, 'PURPOSES_CD', cboPurposes.Value);
	dsOutCertIssu.setColumn(dsOutCertIssu.rowposition, 'RACER_NO', edRacerNo.Text);
	dsOutCertIssu.setColumn(dsOutCertIssu.rowposition, 'RACER_NM', edRacerNm.Text);

	fcSave();
}

